import { Component } from 'react';
import axios from 'axios';

import Page1 from './pages/page1';
import Page2 from './pages/page2';
// import Page3 from './pages/page3';
// import Page4 from './pages/page4';
// import Page5 from './pages/page5';
// import Page6 from './pages/page6';
// import Page7 from './pages/page7';
// import Page8 from './pages/page8';
// import Page9 from './pages/page9';
// import Page10 from './pages/page10';
// import Page11 from './pages/page11';
// import Page12 from './pages/page12';
// import Page13 from './pages/page13';

import larrow from "../pictures/larrow.png";
import rarrow from "../pictures/rarrow.png";
import './dummy.css';
import '../allScripts.css';


class ScriptDummyList extends Component {

    state = {
      button_use: {button_back: false, button_forward: true}, 
      component_list: [<Page1 />, <Page2/>], 
      pagePos: 0,
      allTexts: {}, 
      textDataIsReturned: false
    }


    componentDidMount(){
      axios.get("http://localhost:3001/texts")
      .then(async (text_resp) => {
      console.log('here')
      let texts_output = await text_resp.data 
      this.setState({allTexts: texts_output, textDataIsReturned: true})
    })
    }
    state = {button_use: {button_back: false, button_forward: true}, 
    component_list: [<Page1 />, <Page2 texts={this.state.allTexts} />], 
    pagePos: 0}
    
    handleClick = (letter) => {//This determines which button was pressed
      if (letter === 'b'){
        this.setState((prevState) => ({pagePos: prevState.pagePos - 1}))
      }
      else {
        this.setState((prevState) => ({pagePos: prevState.pagePos + 1}))
      }
      
    }
    render() {
      return(
      <div>
        <ThingsRendered state={this.props.state} />
      </div>
      )
    }
  }


class ThingsRendered extends Component{

  handleChange = (event) => {
    this.setState({text: {...this.state.text, [event.target.id]: {value: event.target.value, id_tag: event.target.id}}})
    if (event.target.id in this.state.text){
        axios.patch(`http://localhost:3001/texts/${this.state.text[event.target.id].id}`, {value: event.target.value, id_tag: event.target.id, script: "dummy"})
}
    else {
        axios.post("http://localhost:3001/texts", {value: event.target.value, id_tag: event.target.id, script: "dummy" })
    }
}

getValue = (id) => {
    for (const i in this.state.text){
        if (this.state.text[i].id_tag === id){
            return this.state.text[i].value;
        }
    }
    return ""
}
  render(){
    return(
      <div className="Script2">
        {this.props.state.component_list[this.props.state.pagePos]}
      </div>
    )
  }
}



export default ScriptDummyList;
