import axios from 'axios';

export const TEXTS_LOADING = "LOADING";
export const TEXTS_SUCCESS = "SUCCESS";
export const FAILURE = "FAILURE";

export const getTexts = (script_number) => dispatch => {
    dispatch({ type: LOADING })
    axios.get('http://localhost:3001/texts')
      .then(res => dispatch({ type: SUCCESS, payload: res.data.filter(text_data => text_data.script === script_number)}))
      .catch(err => dispatch({ type: FAILURE, payload: err}))
}
