import { Component } from 'react';


import allEars from "../../pictures/allears.png"
import pause from "../../pictures/pause.png"
import axios from 'axios';


class Page11 extends Component {
    state = {
        handleclick: {
        five: null,
        otf: null,
        highlight_f: null, 
        highlight_o: null,
        highlight_z: null //These highlights are all about which element gets to be highlighted
        }
    }


    handleClick = (e, letter) => {
       if (letter === 'f'){
                this.setState({handleclick: {oft : null, five: <Five />, highlight_f: "highlighted", highlight_o: null, highlight_z: null}})
            }
            else if (letter === 'o'){
                this.setState({handleclick: {five : null, otf: <OTFZ otf_or_z={'otf'} />, highlight_f: null, highlight_o: "highlighted", highlight_z: null}})
            }
            else if (letter === 'z'){
                this.setState({handleclick: {five : null, otf: <OTFZ otf_or_z={'z'} />, highlight_f: null, highlight_o: null, highlight_z: "highlighted"}})
            }
       
    }

    handleScroll=()=>{
        window.scroll({top:0,behavior:'smooth'})
    
    }
    componentDidMount() {
        this.handleScroll()

    }

    render() {
        
        return (
            <div className="sheet">
                <h1 className="bold center">Reminders about mentoring</h1>
                <div className="left">
                    <div className='container_for_small_margin'>
                        <p>
                            It was really great to talk with you. <br/>
                            <span className='uline'>Mood log reminder</span>
                        </p>
                        <ul>
                            <div className={this.state.handleclick.highlight_f}><li className="squares" onClick={(event) => this.handleClick(event, 'f')}>Your mentee completed 5 mood logs.</li></div>
                            <div className={this.state.handleclick.highlight_o}><li className="squares" onClick={(event) => this.handleClick(event, 'o')}>Your mentee completed 1-4 mood logs.</li></div>
                            <div className={this.state.handleclick.highlight_z}><li className="squares" onClick={(event) => this.handleClick(event, 'z')}>Your mentee completed 0 mood logs.</li></div>
                        </ul>
                    </div>
                    
                    {this.state.handleclick.five}
                    {this.state.handleclick.otf}
                </div>
            </div>
        )
    }
}

class Five extends Component{
    render(){
        return(
        <h2>You did a great job remembering to do your mood logs! Please keep doing them every day! You will keep earning money for doing them.</h2>
        )
    }
}

class OTFZ extends Component{

    state = {
        text: {}
    }

    handleScroll=()=>{
        window.scroll({top:0,behavior:'smooth'})
    
    }
    
    componentDidMount() {
        this.handleScroll()
        axios.get("http://localhost:3001/texts")
        .then(res => {
            const texts = res.data;
            for (const txt of texts){
                if (txt.script === "2"){
                    this.setState({
                        text: {...this.state.text, [txt.id_tag]: txt}
                    })
                }
            }
              
    })
}

    handleChange = (event) => {
        this.setState({text: {...this.state.text, [event.target.id]: {value: event.target.value, id_tag: event.target.id}}})
        if (event.target.id in this.state.text){
            axios.patch(`http://localhost:3001/texts/${this.state.text[event.target.id].id}`, {value: event.target.value, id_tag: event.target.id, script: "2"})
    }
        else {
            axios.post("http://localhost:3001/texts", {value: event.target.value, id_tag: event.target.id, script: "2" })
        }
    }

    getValue = (id) => {
        for (const i in this.state.text){
            if (this.state.text[i].id_tag === id){
                return this.state.text[i].value;
            }
        }
        return ""
    }
        render(){
            const otf_or_z = this.props.otf_or_z
            let otfz1 = null;
            let otfz2 = null;
            if (otf_or_z === 'otf'){
                otfz1 = <h2>This week you did <textarea onChange={event => this.handleChange(event)} id="tad_1_11" className="textarea" defaultValue={this.getValue("tad_1_11")} /> mood logs. Thanks for doing those. Please try to do 5 this week.</h2>
                otfz2 = <textarea onChange={event => this.handleChange(event)} id="tad_2_11" className="textarea" defaultValue={this.getValue("tad_2_11")} />
            }
            else if (otf_or_z === 'z'){
                otfz1 = <h2>I noticed that you did not get a chance to do any of your mood logs</h2>
                otfz2 = <textarea onChange={event => this.handleChange(event)} id="tad_3_11" className="textarea" defaultValue={this.getValue("tad_3_11")} />
            }
        return(
            <div id="otf" className="left">
                <h2>How have you been doing at remembering your mood logs?</h2>
                <img src={pause} alt="Pause" className="pause" />
                <img src={allEars} alt="All ears" className="allEars" />
                <br/><br/>
                {otfz1}
                <h2>Remember, you will earn $5 for every week that you do 5 mood logs. That means you can earn $80 for doing the mood logs during mentoring. </h2>
                <br/><br/>
                <h2>Also, the more you practice, the easier it will be to tell how you feel, so you can be in charge of your mental health.</h2>
                <br/><br/>
                <h2>What do you think is making it hard to do your mood logs? </h2>
                <br/><br/><br/><br/>
                <h2>Is there a way I can help you remember to do them?</h2>
                <br/><br/>
                {otfz2}
            </div>
        )
        }
}



export default Page11;




// This week you did ______ mood logs. Thanks for doing those. Please try to do 5 this week. 
// Remember, you will earn $5 for every week that you do 5 mood logs. That means you can earn $80 for doing the mood logs during mentoring. 

// What do you think is making it hard to do your mood logs? 

 

// Is there a way I can help you remember to do them? 

// [potential text box for mentor to type in to record their response]
