import axios from "axios";

export const userLoginAndCheck = (user_data) => dispatch => {
    axios.post("http://localhost:3001/login", {username: user_data.username, password: user_data.password})
    .then(response => {
        if (response.data.errors){
            dispatch({ type: 'ERRORS_FOUND', payload: response.data.errors});
        }
        else {
            localStorage.setItem("token", response.data.jwt)
            dispatch({type: 'USER_SUCCESS', payload: response.data.id })
        }
    })
} 

export const userSignupAndCheck = (user_data) => dispatch => {
    axios.post("http://localhost:3001/users", {username: user_data.username, email: user_data.email, password: user_data.password, password_confirmation: user_data.password_confirmation})
    .then(response => {
        if (response.data.errors){
            dispatch({ type: 'ERRORS_FOUND', payload: response.data.errors});
        }
        else {
            localStorage.setItem("token", response.data.jwt)
            dispatch({type: 'USER_SUCCESS', payload: response.data.id })
        }
    })
} 


export const userLogout = (user_data) => dispatch => {
    dispatch({ type: 'LOGOUT' });
}
