import { Component } from 'react';
import axios from 'axios';


class Page5 extends Component {
    
    state = {
        text: {}
    }
    handleScroll=()=>{
        window.scroll({top:0,behavior:'smooth'})
    
    }
    componentDidMount() {
        this.handleScroll()
        axios.get("http://localhost:3001/texts")
        .then(res => {
            const texts = res.data;
            for (const txt of texts){
                if (txt.sheet === "2"){
                    this.setState({
                        text: {...this.state.text, [txt.id_tag]: txt}
                    })
                }
            }
              
    })
}

    handleChange = (event) => {
        this.setState({text: {...this.state.text, [event.target.id]: {value: event.target.value, id_tag: event.target.id}}})
        if (event.target.id in this.state.text){
            axios.patch(`http://localhost:3001/texts/${this.state.text[event.target.id].id}`, {value: event.target.value, id_tag: event.target.id, sheet: "2"})
    }
        else {
            axios.post("http://localhost:3001/texts", {value: event.target.value, id_tag: event.target.id, sheet: "2" })
        }
    }

    getValue = (id) => {
        for (const i in this.state.text){
            if (this.state.text[i].id_tag === id){
                return this.state.text[i].value;
            }
        }
        return ""
    }

    render() {
        return (
            <div className="sheet">
                <h1 id="p5title" className="bold center">Learning about mental health conditions</h1>
                <div id="page5" className="left container_for_small_margin">
                    <p>
                        Last week, we talked about the big topic of mental health.<br/>
                        Mental health is all about how we think, feel, and act.  Mental health is about how we handle stress.<br/><br />
                        A mental health condition is when a person is having a hard time with how they think, feel, and act.<br/><br/>
                        You mentioned that mental health is <textarea onChange={event => this.handleChange(event)} id="tad_1_5" className="textarea" defaultValue={this.getValue("tad_1_5")} />
                    </p>
                </div>
            </div>
        )
    }
}
export default Page5;
