import { LOADING, SUCCESS } from '../actions/text/textsAxiosCallAction';

const initialState = {
  texts: null,
  loading: false,
  error: "",
  curatedTextsFromCurrentScript: null
}

export function changeTextReducer(state, action){
    //The purpose of this second reducer is to get the actual value of the text so we change change it and save it here as it's nested 
    switch(action.type) {
        case 'TEXT_UPDATED':
            //Note we need a new value quality given to us with our action
            return {...state, value: action.payload.value}
        default:
            return state
    }
}

export function textReducer(state = initialState, action) {
    /*
    This reducer will do two things: 
    1) Will update the text values saved to the store so that we don't have to make multiple axios calls and 
    2) Will get the initial texts needed in store so that we only have to look through texts with the same script so the time checking isn't as long
    Initial state will be all of the texts that are gotten through axios.
    */
    switch (action.type) {
      //Number 2 from above are accomplished through LOADING and SUCCESS
      case LOADING:
        return {
          ...state, 
          texts: null, 
          loading: true,
          err: '',
          curatedTextsFromCurrentScript: null

        }
      case SUCCESS:
        return {
          ...state,
          texts: action.payload,
          loading: false,
          err: '',
          curatedTextsFromCurrentScript: null
        }

      case 'TEXT_UPDATED': //Number 1 from above
        /* We need to return, in our action, a newContent value and its id_tag (Which we will call "returned_id_tag" and hopefully this is the way to alter our scripts content 
        (Not so sure about the syntax but we'll see) 
        */
       const currentText = this.state.texts.find(text => {return text.id_tag === action.payload.returned_id_tag})
        return { ...state, texts: {...state.texts, currentText: changeTextReducer(currentText, action)} } //Here we will have to have given the action a new script value which would be easy to get
      default:
        // If the reducer doesn't care about this action type,
        // return the existing state unchanged
        return state
    }
  }


