import { Component } from 'react';
import ContentEditable from 'react-contenteditable';

import { connect } from 'react-redux';
// import {getPageData}


class Page1 extends Component {
    
    constructor(props){
        super(props)
        this.state = {
            content: props.pages.find(script_page => {return script_page.page_number === "1"}).content,
            pageID: props.pages.find(script_page => {return script_page.page_number === "1"}).id,
            handleChangeInContentEditable: props.handleChangeInContentEditable,
            handleSubmit: props.handleSubmit,
            handleContentEditableClick: props.handleContentEditableClick
        }
    }

    render() {
        return (
            <div>
                <ContentEditable onClick={event => this.state.handleContentEditableClick(event)} onChange={event => this.state.handleChangeInContentEditable(event)} html={this.state.content} />
                {this.submitButton}
            </div>
        )
    }
    
}

const mapStateToProps = state => {
    return{
      pages: state.pages,
      texts: state.texts,
      pages_loading: state.pages.loading,
      texts_loading: state.texts.loading
    }
  }

  
// export default connect(mapStateToProps, {getPages, getTexts})(App);

export default Page1;
