import React, { Component } from 'react';

import allEars from "../../pictures/allears.png";
import pause from "../../pictures/pause.png";
import stopShare from "../../pictures/stopshare.png";

// import check from "../../pictures/check.png";
// import redX from "../../pictures/redx.png";

import axios from 'axios';

class Page8 extends Component {
    
    state = {
        text: {}
    }
    handleScroll=()=>{
        window.scroll({top:0,behavior:'smooth'})
    
    }
    componentDidMount() {
        this.handleScroll()
        axios.get("http://localhost:3001/texts")
        .then(res => {
            const texts = res.data;
            for (const txt of texts){
                if (txt.sheet === "9"){
                    this.setState({
                        text: {...this.state.text, [txt.id_tag]: txt}
                    })
                }
            }
              
    })
}

    handleChange = (event) => {
        this.setState({text: {...this.state.text, [event.target.id]: {value: event.target.value, id_tag: event.target.id}}})
        if (event.target.id in this.state.text){
            axios.patch(`http://localhost:3001/texts/${this.state.text[event.target.id].id}`, {value: event.target.value, id_tag: event.target.id, sheet: "9"})
    }
        else {
            axios.post("http://localhost:3001/texts", {value: event.target.value, id_tag: event.target.id, sheet: "9" })
        }
    }

    getValue = (id) => {
        for (const i in this.state.text){
            if (this.state.text[i].id_tag === id){
                return this.state.text[i].value;
            }
        }
        return ""
    }

    render() {
        return (
            <div className="sheet">
                <h1 id="p8title" className="bold center">Practicing coping strategies</h1>
                <div id="page8" className='left container_for_small_margin'>
                    <p>
                        We'll  practice your new strategy together.  
                    </p>
                    <div>
                        <h1 className='bold'>Step 1: Identify how you are feeling</h1>
                    </div>








                    <div id="instruction_box_number_1_page_7_sheet_9" className="custom_svg demo_box container_for_small_margin">
                        <p className="top_line_in_instruction_box other_text_in_instruction_box">
                            Send your mentee the link to the website [insert link].<br/>
                            Ask if they want you to share their screen or if they want to share their screen. Help your mentee figure out what to click on. <br/>
                            Then help them look at the solutions.
                        </p>
                    </div>
                    <div className='container_for_medium_margin'>
                        <p>What kind of challenge do you think it was? </p>
                    </div>

                    <div id="instruction_box_number_2_page_7_sheet_9" className="custom_svg demo_box container_for_small_margin">
                        <p className="top_line_in_instruction_box other_text_in_instruction_box">
                            Help your mentee figure out what to click on. <br/>
                            Then help them look at the solutions. 
                        </p>
                    </div>

                    <div className='container_for_small_margin'>
                        <p>
                            Let's read the possible solutions listed.<br/>
                            Do you think any of them would work for you? 
                        </p>
                        <img src={pause} alt="Pause" className="pause" />
                        <img src={allEars} alt="All ears" className="allEars" />
                    </div>

                    <div id="instruction_box_number_3_page_7_sheet_9" className="custom_svg demo_box container_for_large_margin">
                        <p className="top_line_in_instruction_box other_text_in_instruction_box">
                            Help your mentee brainstorm ideas about what solutions they could use. Also give ideas for doing the solution. For example, helping them make a reminder or talking to someone about what they need.<br/><br/>
                            Help them brainstorm ideas. Ask them if they feel like they could use the solutions.<br/> 
                            If they need help figuring out what to do, help them as much as you can. <br/>
                            Give them positive feedback for their ideas. You can say things like:
                        </p>
                        <ul>
                            <li className='li'>Good job!</li>
                            <li className='li'>That's a great idea!</li>
                        </ul>
                    </div>

                    <div className='container_for_extra_small_margin'>
                        <div id="instruction_box_number_4_page_7_sheet_9" className="custom_svg demo_box container_for_large_margin">
                            <p className="top_line_in_instruction_box other_text_in_instruction_box">When you are finished, stop sharing your screen or ask your mentee to stop sharing their screen.</p>
                        </div>
                        <img src={stopShare} alt="Stop sharing" id="stopShare5" />
                    </div>
                    
                </div>
            </div>
        )
    }
}
export default Page8;
