import React, { Component } from 'react';



import axios from 'axios';

class Page13 extends Component {

    state = {
        text: {}
    }
    handleScroll=()=>{
        window.scroll({top:0,behavior:'smooth'})
    
    }
    componentDidMount() {
        this.handleScroll()
        axios.get("http://localhost:3001/texts")
        .then(res => {
            const texts = res.data;
            for (const txt of texts){
                if (txt.sheet === "10"){
                    this.setState({
                        text: {...this.state.text, [txt.id_tag]: txt}
                    })
                }
            }
              
    })
}

    handleChange = (event) => {
        this.setState({text: {...this.state.text, [event.target.id]: {value: event.target.value, id_tag: event.target.id}}})
        if (event.target.id in this.state.text){
            axios.patch(`http://localhost:3001/texts/${this.state.text[event.target.id].id}`, {value: event.target.value, id_tag: event.target.id, sheet: "10"})
    }
        else {
            axios.post("http://localhost:3001/texts", {value: event.target.value, id_tag: event.target.id, sheet: "10" })
        }
    }
    

    getValue = (id) => {
        for (const i in this.state.text){
            if (this.state.text[i].id_tag === id){
                return this.state.text[i].value;
            }
        }
        return ""
    }

  
  

    render() {
        return (
            <div className="sheet">
                <div id="page13" className="left">
                    <h1 id="p1title" className="bold center">Scheduling for next week</h1>
                    
                    <div className='container_for_medium_margin'>
                        <p>
                            Thanks for meeting with me today!<br/>
                            We will meet again on:<textarea onChange={event => this.handleChange(event)} id="text_box_number_1_page_13_sheet_10" className="textarea" defaultValue={this.getValue("text_box_number_1_page_13_sheet_10")} /><br/>
                            This week we will <textarea onChange={event => this.handleChange(event)} id="text_box_number_2_page_13_sheet_10" className="textarea" defaultValue={this.getValue("text_box_number_2_page_13_sheet_10")} />.<br/>
                            <span className='small' id='text_call_email'>(text/call/email—the way you agreed on being in touch)</span>
                            Let’s decide when we will be in touch.
                        </p>
                    </div>
                    
                    <div className='container_for_medium_margin'>
                        <div id="instruction_box_number_1_page_13_sheet_10" className="custom_svg demo_box">
                            <p className="in_inst ital">
                                Schedule a time to get in touch with your mentee.<br/>
                                Here are times you will offer your mentee:<br/>
                                <textarea onChange={event => this.handleChange(event)} id="text_box_number_3_page_13_sheet_10" defaultValue={this.getValue("text_box_number_3_page_13_sheet_10")} />
                            </p>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
export default Page13;
