import axios from "axios";

export const userLoginAndCheck = (user_data) => dispatch => {
    axios.post("http://localhost:3001/login", {username: user_data.username, password: user_data.password})
    .then(response => {
        console.log(response.data)
        if (response.data.errors){
            dispatch({ type: 'ERRORS_FOUND', payload: response.data.errors});
        }
        else {
            dispatch({type: 'USER_SUCCESS', payload: response.data.jwt})
        }
    })
}
