import { Component } from 'react';

import check from "../../../pictures/check.png";
import redX from "../../../pictures/redx.png";


class Step4 extends Component{

    state = {
        handleclick: {
        words_that_appear_when_you_click_green_check: '',
        words_that_appear_when_you_click_red_x: null
        }
    }

        handleClick = (e, letter) => {
            if (letter === 'c'){
                this.setState({handleclick: {words_that_appear_when_you_click_green_check: 
                <p>
                    That's great! 
                    It's great to practice coping strategies even when you're feeling good.  
                    When do you think you'll practice this coping strategy? 
                </p>, 
                words_that_appear_when_you_click_red_x: null}})
            }
            else if (letter === 'x'){
                this.setState({handleclick: {words_that_appear_when_you_click_green_check : '', words_that_appear_when_you_click_red_x: <XPicked />
                    }})
            }
        }
    
    handleScroll=()=>{
        window.scroll({top:0,behavior:'smooth'})
    
    }
    componentDidMount() {
        this.handleScroll()
    }

    
    render(){
        return(
            <div className="sheet">
                <h1 className='bold'>Step 4: Reflecting on how it went</h1>
                <div id="step4" className='container_for_extra_small_margin'>
                    <p>Do you think you could do this activity at home on your own this week?</p>
                    <img className="check" src={check} alt="Check" onClick={(event) => this.handleClick(event, 'c')} /><h2 className="what_does_your_mentor_say">Your mentee says yes</h2>
                    <br/>
                    <h2 className="choicePicked">{this.state.handleclick.words_that_appear_when_you_click_green_check}</h2>
                    <br/><br/>
                    <img className="redX" src={redX} alt="Red X" onClick={(event) => this.handleClick(event, 'x')} /><h2 className="what_does_your_mentor_say">Your mentee says no</h2>
                    <br/>
                    <h2 className="choicePicked">{this.state.handleclick.words_that_appear_when_you_click_red_x}</h2>
                
                    <div className="container_for_medium_margin">
                        <p className="left">What will make it hard to do at home?</p>
                        <p><em>If they're not sure, help them look through the barriers from the card sort.</em></p>
                        
                    </div>
                
                    <div id="instruction_box_number_1_step_5_sheet_10" className="custom_svg demo_box container_for_small_margin">
                        <p className='top_line_in_instruction_box other_text_in_instruction_box'>
                            Help your mentee problem solve how they will deal with the barrier. Use your tools and experiences to help them solve the problem.<br/>
                            You can use the solution website [insert link]
                        </p>
                    </div>

                    <div id="instruction_box_number_2_step_5_sheet_10" className="custom_svg demo_box container_for_small_margin">
                        <p className='top_line_in_instruction_box other_text_in_instruction_box'>
                            After your mentee has an idea for the problem, ask: 
                        </p>
                        <ul>
                            <li className='li'>What time of day would you like to try out the activity?</li>
                            <li className='li'>Is there a place you will try out the coping strategy?</li>
                            <li className='li'>Will you set a reminder to practice the coping strategy [help them to do set the reminder, if they need help]</li>
                        </ul>

                        <div className='container_for_small_margin'>
                            <p className='other_text_in_instruction_box'>Provide encouragement:</p>
                            <ul>
                                <li className='li'>Good idea!</li>
                                <li className='li'>That sounds great!</li>
                            </ul>
                        </div>
                    </div>


                </div>
            </div>
            
        )
    }
}

export default Step4;


class XPicked extends Component{

    render() {
        return (
            <div>
                <p>
                    If your mentee is not sure, ask: 
                </p>
                <ul>
                    <li className='li'>What time of day would you like to try out the activity?</li>
                    <li className='li'>Is there a place you will try out the coping strategy?</li>
                    <li className='li'>Will you set a reminder to practice the coping strategy [help them to do set the reminder, if they need help]</li>
                </ul>
                <div className='container_for_small_margin'>
                    <p>Provide encouragement: </p>
                    <ul>
                        <li className='li'>Good idea!</li>
                        <li className='li'>That sounds great!</li>
                    </ul>
                </div>
	
            </div>
        )
    }
}
