import axios from 'axios';

export const getMentees = (current_user_id) => dispatch => {
    //Gets us a list of all mentees then filters the ones pertaining to the current mentor
    axios.get('http://localhost:3001/mentees')
      .then(res => dispatch({ type: 'GET_MENTEES', payload: res.data.filter(mentee_name => mentee_name.user_id === current_user_id)}))
}

export const flushMenteeList = () => dispatch => {
    //When the user logs out we will no longer be needing this list of mentees so we'd want to get rid of it
    dispatch({ type: 'FLUSH_MENTEE_LIST' })
  }
