import { Component } from 'react';



import axios from 'axios';


class Page4 extends Component {
    
    componentDidMount() {
        window.scroll({top:0,behavior:'smooth'});
    }

    handleChange = (event) => {
        const object_outcome = this.getObject(event.target.id)
        object_outcome === "" ? 
        this.props.postTexts({value: event.target.value, id_tag: event.target.id, mentee_id: this.props.props.mentee_id, script: '6'})
        :
        this.props.patchTexts({value: event.target.value, id_tag: event.target.id, id: object_outcome.id, mentee_id: this.props.props.mentee_id, script: '6'})

    }
    
    getObject = (current_id_tag) => {
        //Returns the object that has the specific id_tag
        let current_text = this.props.texts.find(text_item => {return text_item.id_tag === current_id_tag})
        return current_text ? current_text : ""
    }

    getValue = (current_id_tag) => {
        //Same as getObject but instead it returns the value
        let current_text_for_value = this.props.texts.find(text_item => {return text_item.id_tag === current_id_tag})
        return current_text_for_value ? current_text_for_value.value : ""
    }
        

    render() {
        return (
            <div className="sheet">
                <h1 className="bold center">Icebreaker game/fun activity</h1>
                <div className="left container_for_medium_margin">
                    <textarea onChange={event => this.handleChange(event)} id="text_box_number_1_page_4_script_6" defaultValue={this.getValue("text_box_number_1_page_4_script_6")} />
                </div>
            </div>
        )
    }
}



export default Page4;
