import axios from 'axios';

export const SCRIPT_LOADING = "LOADING";
export const SCRIPT_SUCCESS = "SUCCESS";
export const FAILURE = "FAILURE";

export const getScript = (script_number) => dispatch => {
    dispatch({ type: SCRIPT_LOADING })
    axios.get('http://localhost:3001/scripts')
      .then(res => dispatch({ type: SCRIPT_SUCCESS, payload: res.data.filter(script_element => {return script_element.script === script_number})}))
      .catch(err => dispatch({ type: FAILURE, payload: err}))
}
