import React, { Component } from 'react';


import axios from 'axios';


class Page6 extends Component {

    state = {
        text: {}
    }
    handleScroll=()=>{
        window.scroll({top:0,behavior:'smooth'})
    
    }
    componentDidMount() {
        this.handleScroll()
        axios.get("http://localhost:3001/texts")
        .then(res => {
            const texts = res.data;
            for (const txt of texts){
                if (txt.sheet === "9"){
                    this.setState({
                        text: {...this.state.text, [txt.id_tag]: txt}
                    })
                }
            }
              
    })
}

    handleChange = (event) => {
        this.setState({text: {...this.state.text, [event.target.id]: {value: event.target.value, id_tag: event.target.id}}})
        if (event.target.id in this.state.text){
            axios.patch(`http://localhost:3001/texts/${this.state.text[event.target.id].id}`, {value: event.target.value, id_tag: event.target.id, sheet: "9"})
    }
        else {
            axios.post("http://localhost:3001/texts", {value: event.target.value, id_tag: event.target.id, sheet: "9" })
        }
    }

    getValue = (id) => {
        for (const i in this.state.text){
            if (this.state.text[i].id_tag === id){
                return this.state.text[i].value;
            }
        }
        return ""
    }
  
    render() {
        return (
            <div className="sheet">
                <h1 id="p4title" className="bold center">How did it go?</h1>
                <div id="page6" className="left container_for_small_margin">
                    <p>
                        Last week you practiced your new coping strategy <textarea onChange={event => this.handleChange(event)} id="text_box_number_1_page_6_sheet_9" className="textarea" defaultValue={this.getValue("text_box_number_1_page_6_sheet_9")} />
                    </p>
                    <div id="instruction_box_number_1_page_6_sheet_9" className="custom_svg demo_box container_for_medium_margin">
                        <p className="top_line_in_instruction_box"> 
                            Try to learn about how it was for your mentee to do the coping strategies. Ask questions such, as the questions in the list:<br/>
                            <em>Remember to provide positive feedback and validation.</em>
                            <ul>
                                <li className='li'>How was it do use your coping strategy?</li>
                                <li className='li'>How did it make you feel? </li>
                                <li className='li'>When did you do it?</li>
                                <li className='li'>Did you try using it when you felt ___________________ (feeling they are addressing)</li>
                                <li className='li'>Did you have any challenges using your coping strategy?</li>
                                <li className='li'>Is there anything else you want to share about using your coping strategy?</li>
                            </ul>
                        </p>
                    </div>
                   
                </div>   
            </div>
        )
    }
}


export default Page6;
