import {useState} from 'react'

function LoginForm(props){
    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")

    const handleUsernameChange = (evt) => {
        setUsername(evt.target.value)
    }

    const handlePasswordChange = (evt) => {
        setPassword(evt.target.value)
    }

    const handleSubmit = (evt) => {
        evt.preventDefault()
        axios.post("http://localhost:3001/login", {"token": jwt.token})
        .catch(errors => console.log(errors))
        setUsername("")
        setPassword("")
    }
    
    return(
      <div id="userDiv">
        <h1 className="center">Login</h1>
        <div id="formDiv">
            <form id="loginForm">
                <label htmlFor='username'>Username: </label>
                <input onChange={handleUsernameChange} type="text" className="text" id="username" name="username" autoComplete='username'></input>
                <br/>
                <label htmlFor='password'>Password: </label>
                <input onChange={handlePasswordChange} type="password" className="text" id="password" name="password" autoComplete='password'></input>
                <input name="submit" value="Submit" type="submit" id="submit" onClick={event => handleSubmit(event)}></input>
            </form>
        </div>
      </div>
    )
} 

export default LoginForm
