import { Component } from 'react';

import allEars from "../../pictures/allears.png";
import pause from "../../pictures/pause.png";
import stopShare from "../../pictures/stopshare.png";
import shareScreen from "../../pictures/sharescreen.png";
import axios from 'axios';


class Page6 extends Component {
    
    state= {
        text: {}
    }
    
    handleScroll=()=>{
        window.scroll({top:0,behavior:'smooth'})
    
    }
    componentDidMount() {
        this.handleScroll()
        axios.get("http://localhost:3001/texts")
        .then(res => {
            const texts = res.data;
            for (const txt of texts){
                if (txt.script === "2"){
                    this.setState({
                        text: {...this.state.text, [txt.id_tag]: txt}
                    })
                }
            }
              
    })
}

    handleChange = (event) => {
        this.setState({text: {...this.state.text, [event.target.id]: {value: event.target.value, id_tag: event.target.id}}})
        if (event.target.id in this.state.text){
            axios.patch(`http://localhost:3001/texts/${this.state.text[event.target.id].id}`, {value: event.target.value, id_tag: event.target.id, script: "2"})
    }
        else {
            axios.post("http://localhost:3001/texts", {value: event.target.value, id_tag: event.target.id, script: "2" })
        }
    }

    getValue = (id) => {
        for (const i in this.state.text){
            if (this.state.text[i].id_tag === id){
                return this.state.text[i].value;
            }
        }
        return ""
    }

    render() {
        return (
            <div className="sheet">
                <h1 className="bold center">Mental Health Worksheet</h1>
                <div className="left container_for_medium_margin">
                    <p>What does mental health mean to you? </p>
                    <img className="pause" src={pause} alt="pause" />
                    <img className="allEars" src={allEars} alt="All ears" />
                    <p>
                        Let's work together to find pictures that describe mental health.<br/>
                        You can do a search online or share pictures you have taken. <br/>
                        Or, you can send me an emoji or a GIF to show me what mental health means to you. <br/><br/>
                        There is no right or wrong way to do this. I can help you if you want.<br/><br/>
                        Here is an example.
                    </p>
                    <div id="instruction_box_number_1_page_6_script_2" className="custom_svg demo_box container_for_medium_margin" >
                        <p className="top_line_in_instruction_box other_text_in_instruction_box">
                            Share your screen to show your picture<br/><br/>
                            Link to your picture: <textarea onChange={event => this.handleChange(event)} id="text_box_number_1_page_6_script_2" className="textarea" defaultValue={this.getValue("text_box_number_1_page_6_script_2")} />
                        </p>
                    </div>

                    <img src={shareScreen} alt="Share Screen" id="share_your_screen_page_6_script_2" />
                    <p>I picked this picture because <textarea onChange={event => this.handleChange(event)} id="text_box_number_2_page_6_script_2" className="textarea" defaultValue={this.getValue("text_box_number_2_page_6_script_2")} /></p>
                    <br/>
                    <img src={stopShare} alt="Stop share" id="stop_sharing_screen_page_6_script_2" />
                </div>
            </div>
        )
    }


}
export default Page6;

