import React, { Component } from 'react';



import axios from 'axios';

class Page13 extends Component {

    state = {
        text: {}
    }
    handleScroll=()=>{
        window.scroll({top:0,behavior:'smooth'})
    
    }
    componentDidMount() {
        this.handleScroll()
        axios.get("http://localhost:3001/texts")
        .then(res => {
            const texts = res.data;
            for (const txt of texts){
                if (txt.sheet === "2"){
                    this.setState({
                        text: {...this.state.text, [txt.id_tag]: txt}
                    })
                }
            }
              
    })
}

    handleChange = (event) => {
        this.setState({text: {...this.state.text, [event.target.id]: {value: event.target.value, id_tag: event.target.id}}})
        if (event.target.id in this.state.text){
            axios.patch(`http://localhost:3001/texts/${this.state.text[event.target.id].id}`, {value: event.target.value, id_tag: event.target.id, sheet: "2"})
    }
        else {
            axios.post("http://localhost:3001/texts", {value: event.target.value, id_tag: event.target.id, sheet: "2" })
        }
    }
    

    getValue = (id) => {
        for (const i in this.state.text){
            if (this.state.text[i].id_tag === id){
                return this.state.text[i].value;
            }
        }
        return ""
    }

  
  

    render() {
        return (
            <div className="sheet2">
                <div id="page13" className="left">
                    <h1 id="p1title" className="bold center">Scheduling for next week</h1>
                    <br />
                    <h2>Thanks for meeting with me today!</h2>
                    <h2>We will meet again on:<textarea onChange={event => this.handleChange(event)} id="tad_1_13" className="textarea" defaultValue={this.getValue("tad_1_13")} /></h2>
                    <br/><br/>
                    <h2>This week we will <textarea onChange={event => this.handleChange(event)} id="tad_2_13" className="textarea" defaultValue={this.getValue("tad_2_13")} />.</h2>
                    <h2 className="small" id="tce">(text/call/email—the way you agreed on being in touch)</h2>
                    <br/>
                    <h2>Let’s decide when we will be in touch.</h2>
                    <br/><br/>
                    <div id="inst_1_13" className="custom_svg demo_box">
                        <h2 className="in_inst ital">Schedule a time to get in touch with your mentee.</h2>
                        <br/><br/>
                        <h2 className="in_inst ital">Here are times you will offer your mentee:</h2>
                        <div id="np_1_13">
                            <textarea onChange={event => this.handleChange(event)} id="nt_1_13" defaultValue={this.getValue("nt_1_13")} />
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
export default Page13;
