import React, { Component } from 'react';

import axios from 'axios';

class Page8 extends Component {
    state = {
        
        text: {}
        }
    handleScroll=()=>{
        window.scroll({top:0,behavior:'smooth'})
    
    }
    componentDidMount = () => {
        axios.get("http://localhost:3001/texts")
        .then(res => {
            const texts = res.data;
            for (const txt of texts){
                if (txt.script === "4"){
                    this.setState({
                        text: {...this.state.text, [txt.id_tag]: txt}
                    })
                }
            }      
        })
    }

    handleChange = (event) => {
        this.setState({text: {...this.state.text, [event.target.id]: {value: event.target.value, id_tag: event.target.id}}})
        if (event.target.id in this.state.text){
            axios.patch(`http://localhost:3001/texts/${this.state.text[event.target.id].id}`, {value: event.target.value, id_tag: event.target.id, script: "4"})
    }
        else {
            axios.post("http://localhost:3001/texts", {value: event.target.value, id_tag: event.target.id, script: "4" })
        }
    }
    

    getValue = (id) => {
        for (const i in this.state.text){
            if (this.state.text[i].id_tag === id){
                return this.state.text[i].value;
            }
        }
        return ""
    }
    render() {
        return (
            <div className="sheet">
                <h1 id="p8title" className="bold center">Time for questions</h1>
                <div id="page8" className="left container_for_medium_margin">
                    <p>
                    We are almost done for today.<br/>
                    Do you have any questions or comments about what we talked about?  Is there anything that you wish we could talk more about?
                    </p>
                    <div className='container_for_medium_margin'>
                        <div className='custom_svg demo_box' id='instruction_box_number_1_page_8_script_4'>
                            <p className='top_line_in_instruction_box'>Write their question here:</p>
                        </div>
                    </div>
                    <div className='container_for_small_margin'>
                            <textarea onChange={event => this.handleChange(event)} id="text_box_number_1_page_8_script_4" defaultValue={this.getValue("text_box_number_1_page_8_script_4")} />
                    </div>
                    <div className='container_for_small_margin'>
                        <div className='custom_svg demo_box left' id='instruction_box_number_2_page_8_script_4'>
                            <p className='top_line_in_instruction_box'>If they ask you a question that you don't know the answer to, say: </p>
                        </div>
                        <p className='left'>I don't know the answer to that question. I will ask someone and tell you the answer next time we talk. </p>
                    </div>
                </div>
            </div>
        )
    }
    
}

export default Page8;
