import {useState, useEffect} from 'react';
import axios from 'axios';
import ContentEditable from 'react-contenteditable';

const GeneralPageRenderer = (props) => {
    const [content, setContent] = useState('');
//   const [pageNum, setPageNumber] = useState('');
//   const [scriptNum, setScriptNum] = useState('');
    const [submitButton, setSubmitButton] = useState(null);
    const [scriptID, setScriptID] = useState('');

  const handleClick = (event) => {
      if (submitButton === null){
        setSubmitButton(<button onClick={event => handleSubmit(event)} className="website_change_submit_button">Submit Changes</button>);
      }
    
  }

  const handleSubmit = (event) => {
      event.preventDefault();
      console.log(scriptID);
      axios.patch(`http://localhost:3001/scripts/${scriptID}`, {content: event.target.value})

  }

  useEffect (() => {
    axios.get("http://localhost:3001/scripts")
    .then(res => {
        const scripts = res.data;
        for (const script of scripts){
            if (script.script === "dummy" && script.page_number === "1"){
              setContent(script.content.replaceAll("\\", ""));
              // setPageNumber(script.page_number);
              // setScriptNum(script.script);
              console.log(script.id)
              setScriptID(script.id);
              console.log(scriptID)
            }
        }
})
  }, [scriptID])
  

  return (
      <div>
        <ContentEditable onClick={event => handleClick(event)} html={content} />
        {submitButton}
      </div>
  )
}

export default GeneralPageRenderer
