import React, { Component } from 'react';

import axios from 'axios';


class Page8 extends Component {

    state = {
        text: {}
    }

    handleScroll=()=>{
        window.scroll({top:0,behavior:'smooth'})
    
    }
    componentDidMount() {
        this.handleScroll()
        axios.get("http://localhost:3001/texts")
        .then(res => {
            const texts = res.data;
            for (const txt of texts){
                if (txt.sheet === "3"){
                    this.setState({
                        text: {...this.state.text, [txt.id_tag]: txt}
                    })
                }
            }
              
    })
}

    handleChange = (event) => {
        this.setState({text: {...this.state.text, [event.target.id]: {value: event.target.value, id_tag: event.target.id}}})
        if (event.target.id in this.state.text){
            axios.patch(`http://localhost:3001/texts/${this.state.text[event.target.id].id}`, {value: event.target.value, id_tag: event.target.id, sheet: "3"})
    }
        else {
            axios.post("http://localhost:3001/texts", {value: event.target.value, id_tag: event.target.id, sheet: "3" })
        }
    }

    getValue = (id) => {
        for (const i in this.state.text){
            if (this.state.text[i].id_tag === id){
                return this.state.text[i].value;
            }
        }
        return ""
    }
  
    render() {
        return (
            <div className="sheet">
                <div id="page8" className="left">
                    <h1 id="p8title" className="bold center">Time for questions</h1>
                    <div className='container_for_small_margin'>
                        <p>
                            We are almost done for today. <br/>
                            Do you have any questions or comments about what we talked about? <br/><br/>
                            Write their question here: 
                        </p>
                        <textarea onChange={event => this.handleChange(event)} id="text_box_number_1_page_8" className='textarea' defaultValue={this.getValue("text_box_number_1_page_8")} />
                    </div>

                    <div className='container_for_medium_margin'>
                        <div id="instruction_box_number_1_page_8_sheet_8" className="custom_svg demo_box">
                            <p className="in_inst">
                                <em className='blue_text'>If they ask you a question that you don’t know the answer to, say</em>:<br/> 
                                I don’t know the answer to that question. I will ask someone and tell you the answer next time we talk. 
                            </p>
                        </div>  
                    </div>
                      
                </div>
            </div>
        )
    }
}
export default Page8;
