import axios from 'axios';

export const PAGES_LOADING = "LOADING";
export const PAGES_SUCCESS = "SUCCESS";
export const FAILURE = "FAILURE";

export const getPages = (script_number) => dispatch => {
    dispatch({ type: PAGES_LOADING })
    axios.get('http://localhost:3001/pages')
      .then(res => dispatch({ type: PAGES_SUCCESS, payload: res.data.filter(page_element => {return page_element.script === script_number})}))
      .catch(err => dispatch({ type: FAILURE, payload: err}))
}

export const changePages = (script_number) => dispatch => {
  dispatch({ type: PAGES_LOADING })
  axios.get('http://localhost:3001/pages')
    .then(res => dispatch({ type: PAGES_SUCCESS, payload: res.data.filter(page_element => {return page_element.script === script_number})}))
    .catch(err => dispatch({ type: FAILURE, payload: err}))
}

export const submitButtonClicked = (data) => dispatch => {
  axios.patch(`http://localhost:3001/pages/${data.page.id}`, {content: data.content})
  //Will need to dispatch information to the store
}
