import { Component } from 'react';

import pressrecord from '../../pictures/pressrecord.png'; 
import check from "../../pictures/check.png";
import redX from "../../pictures/redx.png";
import axios from 'axios';

class Page2 extends Component {
    state = {
        handleclick: {
        words_that_appear_when_you_click_green_check: null,
        words_that_appear_when_you_click_red_x: ''
        },
        text: {}
    }
        handleClick = (e, letter) => {
            if (letter === 'c'){
                this.setState({handleclick: {words_that_appear_when_you_click_green_check: <GreenCheckClicked />, words_that_appear_when_you_click_red_x: ''}})
            }
            else if (letter === 'x'){
                this.setState({handleclick: {words_that_appear_when_you_click_green_check : null, words_that_appear_when_you_click_red_x: "Go to the next page"}})
            }
        }

        handleScroll=()=>{
            window.scroll({top:0, behavior:'smooth'})
        }
        
        componentDidMount() {
            this.handleScroll()
            axios.get("http://localhost:3001/texts")
            .then(res => {
                const texts = res.data;
                for (const txt of texts){
                    if (txt.sheet === "2"){
                        this.setState({
                            text: {...this.state.text, [txt.id_tag]: txt}
                        })
                    }
                }
                  
        })
    }

        handleChange = (event) => {
            this.setState({text: {...this.state.text, [event.target.id]: {value: event.target.value, id_tag: event.target.id}}})
            if (event.target.id in this.state.text){
                axios.patch(`http://localhost:3001/texts/${this.state.text[event.target.id].id}`, {value: event.target.value, id_tag: event.target.id, sheet: "2"})
        }
            else {
                axios.post("http://localhost:3001/texts", {value: event.target.value, id_tag: event.target.id, sheet: "2" })
            }
        }
        

        getValue = (id) => {
            for (const i in this.state.text){
                if (this.state.text[i].id_tag === id){
                    return this.state.text[i].value;
                }
            }
            return ""
        }

    render() {
        return (
            <div className="sheet">
                <img src={pressrecord} alt="Press Record" className="left" />
                <h1 className="bold center">Starting the Session</h1>
                <div className="left container_for_medium_margin">
                    <p>
                        Hi, it is good to talk with you again!<br/>
                        As a reminder, I am audio recording this session so I can remember what you say and make sure I am doing a good job. <br />
                        I had a good time talking with you last week. <br/>
                        I enjoyed : <br/>
                        <textarea onChange={event => this.handleChange(event)} id="text_box_number_1_page_2_sheet_2" defaultValue={this.getValue("text_box_number_1_page_2_sheet_2")} /><br/><br/>
                        Was there anything last week you wanted to talk about more? 
                    </p>
                    <div className='container_for_medium_margin'>
                        <img className="check" src={check} alt="Check" onClick={(event) => this.handleClick(event, 'c')} /><p className="what_does_your_mentor_say">Your mentee says yes</p>
                        <br/>
                        <p className="choicePicked">{this.state.handleclick.words_that_appear_when_you_click_green_check}</p>
                        <br/><br/>
                        <img className="redX" src={redX} alt="Red X" onClick={(event) => this.handleClick(event, 'x')} /><p className="what_does_your_mentor_say">Your mentee says no</p>
                        <br/>
                        <p className="choicePicked">{this.state.handleclick.words_that_appear_when_you_click_red_x}</p>
                    </div>
                    
                </div>
            </div>
        )
    }
    
}

class GreenCheckClicked extends Component{
    render(){
        return(
            <div id="checked">
                <p>Talk to your mentee about this topic for up to 5 minutes.
                <br/><br/>
                Once you and your mentee have talked for about five minutes, you can thank them for sharing.  Politely suggest that it's time to transition to hearing about the plan for the day.  If the mentee still wants to talk, suggest “pausing” for now and coming back to the conversation at the end of the peer mentoring session. </p>
            </div>
        )
    }
}
export default Page2;
