import React, { Component } from 'react';



import axios from 'axios';


class Page4 extends Component {
    state = {
        text: {}
    }
    handleScroll=()=>{
        window.scroll({top:0,behavior:'smooth'})
    
    }
    componentDidMount() {
        this.handleScroll()
        axios.get("http://localhost:3001/texts")
        .then(res => {
            const texts = res.data;
            for (const txt of texts){
                if (txt.sheet === "3"){
                    this.setState({
                        text: {...this.state.text, [txt.id_tag]: txt}
                    })
                }
            }
              
    })
}

    handleChange = (event) => {
        this.setState({text: {...this.state.text, [event.target.id]: {value: event.target.value, id_tag: event.target.id}}})
        if (event.target.id in this.state.text){
            axios.patch(`http://localhost:3001/texts/${this.state.text[event.target.id].id}`, {value: event.target.value, id_tag: event.target.id, sheet: "3"})
    }
        else {
            axios.post("http://localhost:3001/texts", {value: event.target.value, id_tag: event.target.id, sheet: "3" })
        }
    }
    

    getValue = (id) => {
        for (const i in this.state.text){
            if (this.state.text[i].id_tag === id){
                return this.state.text[i].value;
            }
        }
        return ""
    }

    render() {
        return (
            <div className="sheet">
                <div id="page4" className="left">
                    <h1 id="p4title" className="bold center">Icebreaker</h1>
                    <br /><br />
                    <h2>We are going to do a scavenger hunt show! </h2>
                    <h2>Let’s take up to 5 minutes to find something in our room or house that makes you feel happy.  For example, I want to show you <textarea onChange={event => this.handleChange(event)} id="tad_1_4" className="textarea" defaultValue={this.getValue("tad_1_4")} /> because when I feel stressed and I use/look at this, it makes me slow down and relax.</h2>
                    <br /><br />
                    <h2>Once you find something in your home that makes you calm or relaxed, bring it back to the screen.</h2>
                    <br /><br />
                    <h2>I’ll put my timer on for 5 minutes and I’ll call out when our time is up.</h2>
                    <br /><br />
                    <h2>If you do need a few more minutes after the timer goes off, just tell me out loud that you’re still looking so I know you need some more time!</h2>
                    <br/><br/><br/><br/>
                    <div id="inst_1_4" className="ital custom_svg demo_box">
                        <h2 className="in_inst rel4">Thank your mentee for sharing.</h2>
                        <br/><br/>
                        <h2>Ask your mentee questions to learn about what they showed you. Some ideas for questions are:</h2>
                        <ul>
                            <li className="li">What is it?</li>
                            <li className="li">Where did you get it from / how did you make it</li>
                            <li className="li">Where is this usually kept in your house?</li>
                            <li className="li">Have you had it for a long time?</li>
                        </ul>
                    </div>
                </div>
            </div>
        )
    }
}
export default Page4;

