import {Component} from 'react';
import axios from 'axios';
import ContentEditable from 'react-contenteditable';



class GeneralPageRenderer extends Component{
    
    state = {
        content: '',
        submitButton: null,
        scriptID: ''
    }

    componentDidMount() {
        axios.get("http://localhost:3001/scripts")
              .then(res => {
                  const scripts = res.data;
                  for (const script of scripts){
                      if (script.script === "dummy" && script.page_number === "1"){
                        this.setState({content: script.content.replaceAll("\\", ""), scriptID: script.id})
                      }
                  }
          })
    }
    handleClick = (event) => {
        if (this.state.submitButton === null){
            this.setState({submitButton: <button onClick={event => this.handleSubmit(event)} className="website_change_submit_button">Submit Changes</button>});
        }
    }

    handleChange = (event) => {
        this.setState({content: event.target.value})
    }

    handleSubmit = (event) => {
        event.preventDefault();
        axios.patch(`http://localhost:3001/scripts/${this.state.scriptID}`, {content: this.state.content})
    }

    render(){
        return(
            <div>
                <ContentEditable onClick={event => this.handleClick(event)} onChange={event => this.handleChange(event)} html={this.state.content} />
                {this.state.submitButton}
            </div>
        )
    }
}


export default GeneralPageRenderer
