import { Component } from 'react';

import axios from 'axios';



class Step3 extends Component{

    state = {
        text: {}
        }

    
    handleScroll=()=>{
        window.scroll({top:0,behavior:'smooth'})
    
    }
    componentDidMount() {
        this.handleScroll()
        axios.get("http://localhost:3001/texts")
        .then(res => {
            const texts = res.data;
            for (const txt of texts){
                if (txt.sheet === "9"){
                    this.setState({
                        text: {...this.state.text, [txt.id_tag]: txt}
                    })
                }
            }
              
    })
}

    render(){
        return(
            <div className="sheet">
                <h1 className='bold'>Step 3: Identify how you are feeling</h1>
                <div id="step3" className='container_for_small_margin'>
                    <p>When we checked in earlier, you said you were feeling <span className='underline_text'>{this.state.text["text_box_number_1_step_1_sheet_9"]}</span></p>
                    <div id="instruction_box_number_1_step_2_sheet_9" className="custom_svg demo_box container_for_extra_small_margin">
                        <p className='top_line_in_instruction_box other_text_in_instruction_box'>
                            Practice the coping strategy together. <br/>
                            You should do the activity for 10-20 minutes.
                        </p>
                    </div>
                
                </div>
            </div>
            
        )
    }
}

export default Step3;
