import { Component } from 'react';

import axios from 'axios';

import check from "../../../pictures/check.png";
import redX from "../../../pictures/redx.png";


class Step5 extends Component{

    state = {
        handleclick: {
        words_that_appear_when_you_click_green_check: '',
        words_that_appear_when_you_click_red_x: null
        },
        text: {}
    }

        handleClick = (e, letter) => {
            if (letter === 'c'){
                this.setState({handleclick: {words_that_appear_when_you_click_green_check: 
                <p>
                    That's great! 
                    It's great to practice coping strategies even when you're feeling good.  
                    When do you think you'll practice this coping strategy? 
                </p>, 
                words_that_appear_when_you_click_red_x: null}})
            }
            else if (letter === 'x'){
                this.setState({handleclick: {words_that_appear_when_you_click_green_check : '', words_that_appear_when_you_click_red_x: <XPicked />
                    }})
            }
        }
    
    handleScroll=()=>{
        window.scroll({top:0,behavior:'smooth'})
    
    }
    componentDidMount() {
        console.log('here');
        this.handleScroll()
        axios.get("http://localhost:3001/texts")
        .then(res => {
            const texts = res.data;
            for (const txt of texts){
                if (txt.sheet === "9"){
                    this.setState({
                        text: {...this.state.text, [txt.id_tag]: txt}
                        
                    })
                    
                }
            }
              
    })

    
    }

    handleChange = (event) => {
        this.setState({text: {...this.state.text, [event.target.id]: {value: event.target.value, id_tag: event.target.id}}})
        if (event.target.id in this.state.text){
            axios.patch(`http://localhost:3001/texts/${this.state.text[event.target.id].id}`, {value: event.target.value, id_tag: event.target.id, sheet: "9"})
    }
        else {
            axios.post("http://localhost:3001/texts", {value: event.target.value, id_tag: event.target.id, sheet: "9" })
        }
    }

    getValue = (id) => {
        for (const i in this.state.text){
            if (this.state.text[i].id_tag === id){
                return this.state.text[i].value;
            }
        }
        return ""
    }

    render(){
        return(
            <div className="sheet">
                <h1 className='bold'>Step 5: Figuring out solutions</h1>
                <div id="step4" className='container_for_extra_small_margin'>
                    <p>Do you think you could do this activity at home on your own this week?</p>
                    <img className="check" src={check} alt="Check" onClick={(event) => this.handleClick(event, 'c')} /><h2 className="what_does_your_mentor_say">Your mentee says yes</h2>
                    <br/>
                    <h2 className="choicePicked">{this.state.handleclick.words_that_appear_when_you_click_green_check}</h2>
                    <br/><br/>
                    <img className="redX" src={redX} alt="Red X" onClick={(event) => this.handleClick(event, 'x')} /><h2 className="what_does_your_mentor_say">Your mentee says no</h2>
                    <br/>
                    <h2 className="choicePicked">{this.state.handleclick.words_that_appear_when_you_click_red_x}</h2>
                
                    <div className="container_for_extra_small_margin solid_box">
                        <p className="top_line_in_instruction_box other_text_in_instruction_box">
                            <em>Give an example from your own life</em><br/><br/>
                            Think about an activity that used to be hard for you. What made it hard?<br/>
                            <textarea onChange={event => this.handleChange(event)} id="text_box_number_1_step_4_sheet_9" className="textarea" defaultValue={this.getValue("text_box_number_1_step_4_sheet_9")} />
                        </p>
                    </div>
                
                    <div className="container_for_extra_small_margin solid_box">
                        <p className="top_line_in_instruction_box other_text_in_instruction_box">
                            <em>Give an example from your own life</em><br/><br/>
                            Think about an activity that made you feel nervous or sad. What was that activity? Why did it make you feel nervous or sad?<br/>
                            <textarea onChange={event => this.handleChange(event)} id="text_box_number_2_step_4_sheet_9" className="textarea" defaultValue={this.getValue("text_box_number_2_step_4_sheet_9")} />
                        </p>
                    </div>
                   
                   <div className='container_for_medium_margin'>
                       <p>We are going to use a card sort to reflect on things that may have made using your coping strategy hard.</p>
                   </div>
                </div>
            </div>
            
        )
    }
}

export default Step5;


