import body from '../pictures/bodynotext.png';
import { connect } from "react-redux";
import { patchTexts, postTexts } from '../../../actions/text';


const BodyDiagram = (props) => {
    const handleChange = (event) => {
        const object_outcome = getObject(event.target.id)
        object_outcome === "" ? 
        props.postTexts({value: event.target.value, id_tag: event.target.id, mentee_id: props.mentee_id, script: props.script})
        :
        props.patchTexts({value: event.target.value, id_tag: event.target.id, id: object_outcome.id, mentee_id: props.mentee_id, script: props.script})

    }
    
    const getObject = (current_id_tag) => {
        //Returns the object that has the specific id_tag
        let current_text = props.texts.find(text_item => {return text_item.id_tag === current_id_tag})
        return current_text ? current_text : ""
    }

    const getValue = (current_id_tag) => {
        //Same as getObject but instead it returns the value
        let current_text_for_value = props.texts.find(text_item => {return text_item.id_tag === current_id_tag})
        return current_text_for_value ? current_text_for_value.value : ""
    }

    return(
        <div className="bodyScanImageDiv">
            <img src={body} alt="Body diagram" id={`body_image_script_${props.script}`} />
            <textarea onChange={event => handleChange(event)} id={`text_box_number_1_body_diagram_script_${props.script}`} className="feelings" defaultValue={getValue(`text_box_number_1_body_diagram_script_${props.script}`)} placeholder='Feeling' />
            <textarea onChange={event => handleChange(event)} id={`text_box_number_2_body_diagram_script_${props.script}`} className="head" defaultValue={getValue(`text_box_number_2_body_diagram_script_${props.script}`)} placeholder='Head' />
            <textarea onChange={event => handleChange(event)} id={`text_box_number_3_body_diagram_script_${props.script}`} className="chest" defaultValue={getValue(`text_box_number_3_body_diagram_script_${props.script}`)} placeholder='Chest' />
            <textarea onChange={event => handleChange(event)} id={`text_box_number_4_body_diagram_script_${props.script}`} className="mouth_and_jaw" defaultValue={getValue(`text_box_number_4_body_diagram_script_${props.script}`)} placeholder='Mouth and jaw' />
            <textarea onChange={event => handleChange(event)} id={`text_box_number_5_body_diagram_script_${props.script}`} className="breathing" defaultValue={getValue(`text_box_number_5_body_diagram_script_${props.script}`)} placeholder='Breathing'/>
            <textarea onChange={event => handleChange(event)} id={`text_box_number_6_body_diagram_script_${props.script}`} className="shoulders" defaultValue={getValue(`text_box_number_6_body_diagram_script_${props.script}`)} placeholder='Shoulders' />
            <textarea onChange={event => handleChange(event)} id={`text_box_number_7_body_diagram_script_${props.script}`} className="stomach" defaultValue={getValue(`text_box_number_7_body_diagram_script_${props.script}`)} placeholder='Stomach' />
            <textarea onChange={event => handleChange(event)} id={`text_box_number_8_body_diagram_script_${props.script}`} className="hands_and_arms" defaultValue={getValue(`text_box_number_8_body_diagram_script_${props.script}`)} placeholder='Hands and arms' />
            <textarea onChange={event => handleChange(event)} id={`text_box_number_9_body_diagram_script_${props.script}`} className="legs_and_feet" defaultValue={getValue(`text_box_number_9_body_diagram_script_${props.script}`)} placeholder='Legs and feet' />
            <textarea onChange={event => handleChange(event)} id={`text_box_number_10_body_diagram_script_${props.script}`} className="fidgeting" defaultValue={getValue(`text_box_number_10_body_diagram_script_${props.script}`)} placeholder='Fidgeting' />
            <textarea onChange={event => handleChange(event)} id={`text_box_number_11_body_diagram_script_${props.script}`} className="energy_level" defaultValue={getValue(`text_box_number_11_body_diagram_script_${props.script}`)} placeholder='Change in energy level' />
        </div> 
    )
}

const mapStateToProps = state => {
    return{
        texts: state.texts.curatedTextsFromCurrentScript,
        mentee_id: state.mentees.current_mentee_id,
        script: state.texts.currentScript
    }
}

const mapDispatchToProps = dispatch => {
    return{
        patchTexts: (text_data) => dispatch(patchTexts(text_data)),
        postTexts: (text_data) => dispatch(postTexts(text_data))

    }
}

export default connect(mapStateToProps, mapDispatchToProps)(BodyDiagram);
