import {Component} from 'react';
import ContentEditable from 'react-contenteditable';

import { connect } from 'react-redux';

import { contentClicked, submitButtonClicked } from '../../../../actions/pages/contentEditableOrSubmitClicked';


class GeneralPageRenderer extends Component{
    
    state = {
        page: null,
        page_content: null,
        submitButton: null
    }
    componentDidMount(){
        //For now, this is set. In the future, we will need to convey some way to suggest that something is changing and thus we will need to 
        //Account for that by way of a reducer/action
        const pageData = this.props.pages.find(page_data => {return page_data.page_number === this.props.currentPageNumber.toString()})
        this.setState({page: pageData, page_content: pageData.content})
    }

    handleChangeInContentEditable = (event) => {
        this.setState({content: event.target.value})
    }

    render(){
        if (this.props.submitButtonStatus === false){
            this.setState({submitButton: null})
        }
        else if (this.props.submitButtonStatus === true){
            this.setState({submitButton: <button onClick={event => this.props.submitButtonClicked(this.state)} className="website_change_submit_button">Submit Changes</button>})
        }
        if (this.state.content === null){
            return(
                <h1>Loading</h1>
            )
        }
        return(
            <div>
                <ContentEditable onClick={event => this.props.contentClicked()} onChange={event => this.handleChangeInContentEditable(event)}
                html={this.state.content} />
                {this.state.submitButton}
            </div>
        )
    }
}

const mapStateToProps = state => {
    return{
      pages: state.pages,
      texts: state.texts,
      currentPageNumber: state.currentPageNumber,
      submitButtonStatus: state.submitButtonStatus
    }
  }

  

export default connect(mapStateToProps, {contentClicked, submitButtonClicked})(GeneralPageRenderer);
