import { PAGES_LOADING, PAGES_SUCCESS } from '../actions/pages/pagesAxiosCallAction';

const initialState = {
  loading: false,
  error: "",
  pagesChosen: null,
  submitButton: false,
  currentPageNumber: 1
}



export default function pageReducer(state = initialState, action) {
    /*
    This reducer will do two things (Based on two separate actions): 
    1) Will update the pages values saved to the store so that we don't have to make multiple axios calls and 
    2) Will give the option (when we ultimately implement the functionality of letting them decide 
        to change the script they're working on without reloading the app alltogether) to change which script they're looking at
    Initial state will be the single state chosen by button press.
    */
    switch (action.type) {
      //Number 2 from above are accomplished through LOADING and SUCCESS
      case PAGES_LOADING:
        return {
          ...state, 
          loading: true,
          err: '',
          pagesChosen: null

        }
      case PAGES_SUCCESS:
        return {
          ...state,
          loading: false,
          err: '',
          pagesChosen: action.payload,
          currentPageNumber: 1
        }
      case 'CONTENTEDITABLE_CLICKED':
        //This will be when the content editable thing will be clicked. It will make it so button is displayed
        return({...state, submitButton: true}) 
      case 'PAGE_CHANGE_SUBMITTED': //Number 1 from above
        /* We need to return, in our action, a newContent value and hopefully this is the way to alter our pages' content 
        (Not so sure about the syntax but we'll see) 
        */
        return { ...state, pagesChosen: {...state.pagesChosen, content: action.payload} } //Here we will have to have given the action a new page value which would be easy to get
      
      default:
        // If the reducer doesn't care about this action type,
        // return the existing state unchanged
        return state
    }
  }
