import { Component } from 'react';

import axios from 'axios';

import body from '../../../pictures/body.png';
import phone from '../../../pictures/phone.png'


class Step4 extends Component{

    state = {
        text: {}
        }

    
    handleScroll=()=>{
        window.scroll({top:0,behavior:'smooth'})
    
    }
    componentDidMount() {
        console.log('here');
        this.handleScroll()
        axios.get("http://localhost:3001/texts")
        .then(res => {
            const texts = res.data;
            for (const txt of texts){
                if (txt.sheet === "9"){
                    this.setState({
                        text: {...this.state.text, [txt.id_tag]: txt}
                        
                    })
                    
                }
            }
              
    })

    
    }

    getValue = (id) => {
        for (const i in this.state.text){
            if (this.state.text[i].id_tag === id){
                return this.state.text[i].value;
            }
        }
        return ""
    }

    render(){
        return(
            <div className="sheet">
                <h1 className='bold'>Step 4: Reflecting on how it went</h1>
                <div id="step4" className='container_for_small_margin'>
                    <p>
                    Sometimes it can be hard to do a coping strategy because things get in the way.
                        <br/><br/>
                        Let's think about how you feel again. It's good to think about how you are feeling after you do a coping strategy to see if using the coping strategy changed how you feel.
                    </p>
                
                    <div id="instruction_box_number_1_step_3_sheet_9" className="custom_svg demo_box container_for_extra_small_margin">
                        <p className='top_line_in_instruction_box other_text_in_instruction_box'>
                            Help your mentee review their body scan worksheet or mood log.<br/>
                            Put the link in the chat and ask your mentee to screen share
                        </p>
                    </div>
                
                    <div id="image_div_step_3_sheet_9">
                        <img src={phone} id="phone_step_3_sheet_9" alt="Phone with emojis" />
                        <img src={body} alt="Body diagram" id="body_diagram_step_3_sheet_9" />
                    </div>
                </div>
            </div>
            
        )
    }
}

export default Step4;
