import React, { Component } from 'react';


import allEars from "../../pictures/allears.png"
import pause from "../../pictures/pause.png"
import axios from 'axios';


class Page11 extends Component {
    state = {
        handleclick: {
        five: null,
        otf: null,
        highlight_f: null, 
        highlight_o: null,
        highlight_z: null //These highlights are all about which element gets to be highlighted
        }
    }

    handleScroll=()=>{
        window.scroll({top:0,behavior:'smooth'})
    
    }
    
    componentDidMount() {
        this.handleScroll()
        axios.get("http://localhost:3001/texts")
        .then(res => {
            const texts = res.data;
            for (const txt of texts){
                if (txt.sheet === "9"){
                    this.setState({
                        text: {...this.state.text, [txt.id_tag]: txt}
                    })
                }
            }
              
    })
}

    handleChange = (event) => {
        this.setState({text: {...this.state.text, [event.target.id]: {value: event.target.value, id_tag: event.target.id}}})
        if (event.target.id in this.state.text){
            axios.patch(`http://localhost:3001/texts/${this.state.text[event.target.id].id}`, {value: event.target.value, id_tag: event.target.id, sheet: "9"})
    }
        else {
            axios.post("http://localhost:3001/texts", {value: event.target.value, id_tag: event.target.id, sheet: "9" })
        }
    }

    getValue = (id) => {
        for (const i in this.state.text){
            if (this.state.text[i].id_tag === id){
                return this.state.text[i].value;
            }
        }
        return ""
    }

    handleClick = (e, letter) => {
       if (letter === 'f'){
                this.setState({handleclick: {oft : null, five: <Five />, highlight_f: "highlighted", highlight_o: null, highlight_z: null}})
            }
            else if (letter === 'o'){
                this.setState({handleclick: {five : null, otf: <OTFZ otf_or_z={'otf'} />, highlight_f: null, highlight_o: "highlighted", highlight_z: null}})
            }
            else if (letter === 'z'){
                this.setState({handleclick: {five : null, otf: <OTFZ otf_or_z={'z'} />, highlight_f: null, highlight_o: null, highlight_z: "highlighted"}})
            }
       
    }

   
    
    
  
    render() {
        
        return (
            <div className="sheet">
                <div id="page11" className="left">
                    <h1 id="p1title" className="bold center">Reminders about mentoring</h1>
                    <div className='container_for_medium_margin'>
                        <p>
                            It was really great to talk with you. <br/>
                            <span className='uline'>Mood log reminder</span>
                        </p>
                    </div>
                    <ul>
                        <div className={this.state.handleclick.highlight_f}><li className="li squares" onClick={(event) => this.handleClick(event, 'f')}>Your mentee completed 5 mood logs.</li></div>
                        <div className={this.state.handleclick.highlight_o}><li className="li squares" onClick={(event) => this.handleClick(event, 'o')}>Your mentee completed 1-4 mood logs.</li></div>
                        <div className={this.state.handleclick.highlight_z}><li className="li squares" onClick={(event) => this.handleClick(event, 'z')}>Your mentee completed 0 mood logs.</li></div>
                    </ul>
                    {this.state.handleclick.five}
                    {this.state.handleclick.otf}

                    <div className='container_for_large_margin'>
                        <p>
                            <span className='uline'>Practice reminder</span> <br/>
                            This week, your job will be to practice using a coping strategy. We decided that you wanted to try   
                            <textarea onChange={event => this.handleChange(event)} id="text_box_number_1_page_11" className="textarea" defaultValue={this.getValue("text_box_number_1_page_11")} />.
                            <span className='small' id="small_text_under_textarea_strategy_practiced">(the strategy you practiced today)</span><br/>
                            Try to do this activity at least 5 times this week. <br/>
                            What kind of help do you think you will need to remember to practice the activity? Here are some ideas:
                        </p>
                        <ul>
                            <li className='squares li'>A reminder</li>
                            <li className='squares li'>Setting an alarm on your phone</li>
                            <li className='squares li'>Something else <textarea onChange={event => this.handleChange(event)} id="text_box_number_2_page_11" className="textarea" defaultValue={this.getValue("text_box_number_2_page_11")} />.
</li>
                        </ul>
                        <p>
                            If you have challenges with your coping strategy, you can use the solutions website.<br/><br/>
                            The mood log will help you keep track of how you feel after you do the activity. You can use body scan worksheet if it helps you with the mood logs.
                        </p>
                    </div>

                </div>
            </div>
        )
    }
}

class Five extends Component{
    render(){
        return(
        <h2>You did a great job remembering to do your mood logs! Please keep doing them every day! You will keep earning money for doing them.</h2>
        )
    }
}

class OTFZ extends Component{

    state = {
        text: {}
    }

    handleScroll=()=>{
        window.scroll({top:0,behavior:'smooth'})
    
    }
    
    componentDidMount() {
        this.handleScroll()
        axios.get("http://localhost:3001/texts")
        .then(res => {
            const texts = res.data;
            for (const txt of texts){
                if (txt.sheet === "9"){
                    this.setState({
                        text: {...this.state.text, [txt.id_tag]: txt}
                    })
                }
            }
              
    })
}

    handleChange = (event) => {
        this.setState({text: {...this.state.text, [event.target.id]: {value: event.target.value, id_tag: event.target.id}}})
        if (event.target.id in this.state.text){
            axios.patch(`http://localhost:3001/texts/${this.state.text[event.target.id].id}`, {value: event.target.value, id_tag: event.target.id, sheet: "9"})
    }
        else {
            axios.post("http://localhost:3001/texts", {value: event.target.value, id_tag: event.target.id, sheet: "9" })
        }
    }

    getValue = (id) => {
        for (const i in this.state.text){
            if (this.state.text[i].id_tag === id){
                return this.state.text[i].value;
            }
        }
        return ""
    }
        render(){
            const otf_or_z = this.props.otf_or_z
            let otfz1 = null;
            let otfz2 = null;
            if (otf_or_z === 'otf'){
                otfz1 = <h2>This week you did <textarea onChange={event => this.handleChange(event)} id="text_box_number_2_page_11" className="textarea" defaultValue={this.getValue("text_box_number_2_page_11")} /> mood logs. Thanks for doing those. Please try to do 5 this week.</h2>
                otfz2 = <textarea onChange={event => this.handleChange(event)} id="text_box_number_3_page_11" className="textarea" defaultValue={this.getValue("text_box_number_3_page_11")} />
            }
            else if (otf_or_z === 'z'){
                otfz1 = <h2>I noticed that you did not get a chance to do any of your mood logs</h2>
                otfz2 = <textarea onChange={event => this.handleChange(event)} id="text_box_number_4_page_11" className="textarea" defaultValue={this.getValue("text_box_number_4_page_11")} />
            }
        return(
            <div id="otf" className="left">
