import {useState, useEffect} from 'react';
import axios from 'axios';

const GeneralPageRenderer = (props) => {
  const [isEditingField, setIsEditingField] = useState(false)
  const [content, setContent] = useState('')

  const handleClick = (event) => {
    setIsEditingField(!isEditingField)
  }

  const getSheets = () => {
      const response = axios.get("http://localhost:3001/sheets")
      .then(res => {
          const sheets = res.data;
          let flag = false;
          for (const sht of sheets){
              if (sht.script === "dummy" && sht.page_number === "1"){
                  setContent(sht.content)
              }
          }
  })
}

  useEffect (() => {getSheets()})
  

  return (
      {content}
  )
}
