import { LOADING, SUCCESS } from '../actions/script/scriptAxiosCallAction';

const initialState = {
  script: null,
  loading: false,
  error: ""
}



export default function scriptReducer(state = initialState, action) {
    /*
    This reducer will do two things (Based on two separate actions): 
    1) Will update the script values saved to the store so that we don't have to make multiple axios calls and 
    2) Will give the option (when we ultimately implement the functionality of letting them decide 
        to change the script they're working on without reloading the app alltogether) to change which script they're looking at
    Initial state will be the single state chosen by button press.
    */
    switch (action.type) {
      case LOADING:
        return {
          ...state, 
          script: null, 
          loading: true,
          err: ''

        }
      case SUCCESS:
        return {
          ...state,
          script: action.payload
        }
      case 'SCRIPT_UPDATED': //Number 1 from above
        /* We need to return, in our action, a newContent value and hopefully this is the way to alter our scripts content 
        (Not so sure about the syntax but we'll see) 
        */
        return { ...state, script: {...state.script, content: action.payload.newContent} } //Here we will have to have given the action a new script value which would be easy to get
      case 'SCRIPT_PICKED': //Number 2 from above
      /* We'll need to give our action a newScript value. Used in the initial button press */
        return { ...state, script: action.payload.script }
      default:
        // If the reducer doesn't care about this action type,
        // return the existing state unchanged
        return state
    }
  }
