import { Component } from 'react';
// import axios from 'axios';

import Page1 from './pages/page1';
import Page2 from './pages/page2';
import Page3 from './pages/page3';
import Page4 from './pages/page4';
// import Page5 from './pages/page5';
// import Page6 from './pages/page6';
// import Page7 from './pages/page7';
// import Page8 from './pages/page8';
// import Page9 from './pages/page9';
// import Page10 from './pages/page10';
// import Page11 from './pages/page11';
// import Page12 from './pages/page12';
// import Page13 from './pages/page13';

import larrow from "../pictures/larrow.png";
import rarrow from "../pictures/rarrow.png";
import './dummy.css';
import '../allScripts.css';


class ScriptDummyList extends Component {

    constructor(props){
      super(props)
      this.state = {
        texts: props.text, 
        scripts: props.scripts
      }
  
    }
    
    render() {
      return(
      <div>
        <ThingsRendered state={this.state} />
      </div>
      )
    }
  }


class ThingsRendered extends Component{
  constructor(props){
    super(props)
    this.state = {
      allTexts: props.state.texts,
      scripts: props.state.scripts,
      button_use: {button_back: false, button_forward: true}, 
      component_list: [<Page1 />, <Page2 text={this.state.texts} />], 
      pagePos: 0,
    }
  }

  componentDidMount(){
    //Here I will place stuff so that we can put the correct data in each component that we're passing so they know what to render
    //Need to look up how to conditionally set the props of a component so that I can do this in a for loop instead of having to do it manually for each component
    
    // for (const component of this.state.component_list){
    //   this.setState({component_: {...this.state.text, [event.target.id]: {value: event.target.value, id_tag: event.target.id}}})
    // }
  }

  handleClick = (letter) => {//This determines which button was pressed
    if (letter === 'b'){
      this.setState((prevState) => ({pagePos: prevState.pagePos - 1}))
    }
    else {
      this.setState((prevState) => ({pagePos: prevState.pagePos + 1}))
    }
    
  }

  render(){
    console.log(this.props)
    return(
      <div className="Script2">
        {this.state.component_list[this.state.pagePos]}
        <div id="buttonDiv">
          {this.state.pagePos === 0 ? null :  <img alt="left arrow back" src={larrow} onClick={() => this.handleClick('b')} className="left arrow button"/>}
          {this.state.pagePos === this.state.component_list.length - 1 ? null :  <img alt="right arrow forward" src={rarrow} onClick={() => this.handleClick('f')} className="right arrow button"/>}
        </div>
        <h3 className="page_num">{this.state.pagePos + 1}</h3>
      </div>
    )
  }
}



export default ScriptDummyList;
