import body from '../pictures/bodynotext.png';
import { connect } from "react-redux";
import { patchTexts, postTexts } from '../../../actions/text';

import '../extcss.scss';

const BodyDiagram = (props) => {
    const handleChange = (event) => {
        const object_outcome = getObject(event.target.id)
        object_outcome === "" ? 
        props.postTexts({value: event.target.value, id_tag: event.target.id, mentee_id: props.mentee_id, script: props.script})
        :
        props.patchTexts({value: event.target.value, id_tag: event.target.id, id: object_outcome.id, mentee_id: props.mentee_id, script: props.script})

    }
    
    const getObject = (current_id_tag) => {
        //Returns the object that has the specific id_tag
        let current_text = props.texts.find(text_item => {return text_item.id_tag === current_id_tag})
        return current_text ? current_text : ""
    }

    const getValue = (current_id_tag) => {
        //Same as getObject but instead it returns the value
        let current_text_for_value = props.texts.find(text_item => {return text_item.id_tag === current_id_tag})
        return current_text_for_value ? current_text_for_value.value : ""
    }

    return(
        <div className="bodyScanImageDiv">
            <img src={body} alt="Body diagram" id={`body_image_script_${props.script}`} />
            <form>
                <div className='feelings_div'>
                    <label htmlFor='feelings' className="feelings_label">Feelings</label><br/>
                    <input type="text" name="feelings" onChange={event => handleChange(event)} id={`text_box_number_1_body_diagram_script_${props.script}`} className="feelings" defaultValue={getValue(`text_box_number_1_body_diagram_script_${props.script}`)}/>
                </div>

                <div className='head_div'>
                    <label htmlFor='head' className="head_label">Head</label>
                    <input type="text" name="head" onChange={event => handleChange(event)} id={`text_box_number_2_body_diagram_script_${props.script}`} className="head" defaultValue={getValue(`text_box_number_2_body_diagram_script_${props.script}`)}/>
                </div>
                
                <div className='chest_div'>
                    <label htmlFor='chest' className="chest_label">Chest</label>
                    <input type="text" name="chest" onChange={event => handleChange(event)} id={`text_box_number_3_body_diagram_script_${props.script}`} className="chest" defaultValue={getValue(`text_box_number_3_body_diagram_script_${props.script}`)}/>
                </div>

                <div className='mouth_and_jaw_div'>
                    <label htmlFor='mouth_and_jaw' className="mouth_and_jaw_label">Mouth and jaw</label>
                    <input type="text" name="mouth_and_jaw" onChange={event => handleChange(event)} id={`text_box_number_4_body_diagram_script_${props.script}`} className="mouth_and_jaw" defaultValue={getValue(`text_box_number_4_body_diagram_script_${props.script}`)}/>
                </div>

                <div className='breathing_div'>
                    <label htmlFor='breathing' className="breathing_label">Breathing</label>
                    <input type="text" name="breathing" onChange={event => handleChange(event)} id={`text_box_number_5_body_diagram_script_${props.script}`} className="breathing" defaultValue={getValue(`text_box_number_5_body_diagram_script_${props.script}`)}/>
                </div>

                <div className='shoulder_div'>
                    <label htmlFor='shoulders' className="shoulders_label">Shoulder</label>
                    <input type="text" name="shoulders" onChange={event => handleChange(event)} id={`text_box_number_6_body_diagram_script_${props.script}`} className="shoulders" defaultValue={getValue(`text_box_number_6_body_diagram_script_${props.script}`)}/>
                </div>

                <div className='stomach_div'>
                    <label htmlFor='stomach' className="stomach_label">Stomach</label>
                    <input type="text" name="stomach" onChange={event => handleChange(event)} id={`text_box_number_7_body_diagram_script_${props.script}`} className="stomach" defaultValue={getValue(`text_box_number_7_body_diagram_script_${props.script}`)}/>
                </div>

                <div className='hands_and_arms_div'>
                    <label htmlFor='hands_and_arms' className="hands_and_arms_label">Hands and arms</label>
                    <input type="text" name="hands_and_arms" onChange={event => handleChange(event)} id={`text_box_number_8_body_diagram_script_${props.script}`} className="hands_and_arms" defaultValue={getValue(`text_box_number_8_body_diagram_script_${props.script}`)}/>
                </div>

                <div className='legs_and_feet_div'>
                    <label htmlFor='legs_and_feet' className="legs_and_feet_label">Legs and feet</label>
                    <input type="text" name="legs_and_feet" onChange={event => handleChange(event)} id={`text_box_number_9_body_diagram_script_${props.script}`} className="legs_and_feet" defaultValue={getValue(`text_box_number_9_body_diagram_script_${props.script}`)}/> 
                </div>
                
                <div className='fidgeting_div'>
                    <label htmlFor='fidgeting' className="fidgeting_label">Fidgeting</label>
                    <input type="text" name="fidgeting" onChange={event => handleChange(event)} id={`text_box_number_10_body_diagram_script_${props.script}`} className="fidgeting" defaultValue={getValue(`text_box_number_10_body_diagram_script_${props.script}`)}/>
                </div>

                <div className='energy_level_div'>
                    <label htmlFor='energy_level' className="energy_level_label">Change in energy level</label>
                    <input type="text" name="energy_level" onChange={event => handleChange(event)} id={`text_box_number_11_body_diagram_script_${props.script}`} className="energy_level" defaultValue={getValue(`text_box_number_11_body_diagram_script_${props.script}`)}/>
                </div>
            </form>
        </div> 
    )
}

const mapStateToProps = state => {
    return{
        texts: state.texts.curatedTextsFromCurrentScript,
        mentee_id: state.mentees.current_mentee_id,
        script: state.texts.currentScript
    }
}

const mapDispatchToProps = dispatch => {
    return{
        patchTexts: (text_data) => dispatch(patchTexts(text_data)),
        postTexts: (text_data) => dispatch(postTexts(text_data))

    }
}

export default connect(mapStateToProps, mapDispatchToProps)(BodyDiagram);
