import React, { Component } from 'react';

import body from '../../pictures/body.png'
import axios from 'axios';


class Page7 extends Component {

    state = {
        text: {}
    }
    
    handleScroll=()=>{
        window.scroll({top:0,behavior:'smooth'})
    }
    
    componentDidMount() {
        this.handleScroll()
        axios.get("http://localhost:3001/texts")
        .then(res => {
            const texts = res.data;
            for (const txt of texts){
                if (txt.sheet === "5"){
                    this.setState({
                        text: {...this.state.text, [txt.id_tag]: txt}
                    })
                }
            }
              
    })
}

    handleChange = (event) => {
        this.setState({text: {...this.state.text, [event.target.id]: {value: event.target.value, id_tag: event.target.id}}})
        if (event.target.id in this.state.text){
            axios.patch(`http://localhost:3001/texts/${this.state.text[event.target.id].id}`, {value: event.target.value, id_tag: event.target.id, sheet: "5"})
    }
        else {
            axios.post("http://localhost:3001/texts", {value: event.target.value, id_tag: event.target.id, sheet: "5" })
        }
    }

    getValue = (id) => {
        for (const i in this.state.text){
            if (this.state.text[i].id_tag === id){
                return this.state.text[i].value;
            }
        }
        return ""
    }
  
    render() {
        return (
            <div className="sheet">
                <div id="page7" className="left">
                    <h1 id="p1title" className="bold center">Review</h1>
                    <br />
                    <div id="bodyScanImageDiv">
                        <p>
                            Last week, we worked on the body scan worksheet. 
                            <br/><br/><br/>
                            Did you try using the body scan worksheet on your own?
                            <br/><br/><br/>
                            What do you think about it?
                            <br/>
                            There are no right or wrong answers.
                            <br/><br/><br/>
                            Is any part of it hard? 
                        </p>
                        <img src={body} alt="Body" id='body7' />
                    </div>
                    <br/>
                    
                    <p>Is there anything you don’t like about it?</p>
                    <textarea onChange={event => this.handleChange(event)} id="nt_2_7" defaultValue={this.getValue("nt_2_7")} />
                    <p>Is there any way I can help make it easier for you?</p>
                    <div id="np_3_7">
                        <textarea onChange={event => this.handleChange(event)} id="nt_3_7" defaultValue={this.getValue("nt_3_7")} />
                    </div>
                    <p>
                        Let’s practice using it again together. 
                        <br/><br/>
                        How are you feeling right now?
                    </p>
                    <div id="inst_1_7" className="custom_svg demo_box">
                        <p className="in_inst">
                            Help your mentee review their body scan worksheet.
                            <br/><br/>
                            Put the link in the chat and ask your mentee to screen share
                        </p>
                    </div>
                    <br/><br/>
                    <p>
                        Let’s think about how each part of your body feels.
                    </p>
                    <div id="inst_2_7" className="custom_svg demo_box">
                        <p className="in_inst">
                            If your mentee needs help, ask them some questions. You can use the questions below or use the long list of questions you have in your binder.
                        </p>
                    </div>
                    <br/><br/>
                    <ul>
                        <li className="li">How does your chest feel?</li>
                        <li className="li">How is your breathing?</li>
                        <li className="li">How do your mouth and jaw feel?</li>
                        <li className="li">How do your shoulders feel?</li>
                        <li className="li">How do your legs and feet feel?</li>
                        <li className="li">Do you notice yourself fidgeting?</li>
                        <li className="li">Do you notice a change in your energy level? </li>
                        <li className="li op_circles" id="op7">Do you feel like you have more or less energy than usual?</li>
                    </ul>
                    <br/><br/><br/>
                    <p>
                        Do you have any questions about using the body scan worksheet?
                    </p>
                    
                    <div id="np_4_7">
                        <textarea onChange={event => this.handleChange(event)} id="nt_4_7" defaultValue={this.getValue("nt_4_7")} />
                    </div>
                    <br/><br/><br/>
                    <p>
                        You can keep practicing this week to learn more about your mental health. 
                    </p>
                </div>
            </div>
        )
    }
}
export default Page7;
