import React, { Component } from 'react';

import check from "../../pictures/check.png";
import redX from "../../pictures/redx.png";

import axios from 'axios';


class Page4 extends Component {
    state = {
        handleclick: {
            words_that_appear_when_you_click_green_check: null,
            words_that_appear_when_you_click_red_x: null
        }
    }
        handleClick = (e, letter) => {
            if (letter === 'c'){
                this.setState({handleclick: {words_that_appear_when_you_click_green_check: <GreenCheckClicked />, words_that_appear_when_you_click_red_x: null}})
            }
            else if (letter === 'x'){
                this.setState({handleclick: {words_that_appear_when_you_click_green_check : null, words_that_appear_when_you_click_red_x: <RedXClicked />}})
            }
        }

    handleScroll=()=>{
        window.scroll({top:0,behavior:'smooth'})
    
    }
    componentDidMount() {
        this.handleScroll()
    }
        

    render() {
        return (
            <div className="sheet">
                <h1 id="p4title" className="bold center">Icebreaker game/fun activity</h1>
                <div id="page4" className="left container_for_medium_margin">
                    <h2>Would you like to do another scavenger hunt show and tell? </h2>
                    <div className="container_for_medium_margin">
                        <img className="check" src={check} alt="Check" onClick={(event) => this.handleClick(event, 'c')} /><h2 className="what_does_your_mentor_say">Your mentee says yes</h2>
                        <br/>
                        <h2 className="choicePicked">{this.state.handleclick.words_that_appear_when_you_click_green_check}</h2>
                        <br/><br/>
                        <img className="redX" src={redX} alt="Red X" onClick={(event) => this.handleClick(event, 'x')} /><h2 className="what_does_your_mentor_say">Your mentee says no</h2>
                        <br/>
                        <h2 className="choicePicked">{this.state.handleclick.words_that_appear_when_you_click_red_x}</h2>
                    </div>
                </div>
            </div>
        )
    }
}

class GreenCheckClicked extends Component {

    state = {
        text: {}
    }

    componentDidMount = () => {
        axios.get("http://localhost:3001/texts")
        .then(res => {
            const texts = res.data;
            for (const txt of texts){
                if (txt.sheet === "4"){
                    this.setState({
                        text: {...this.state.text, [txt.id_tag]: txt}
                    })
                }
            }      
        })
    }

    handleChange = (event) => {
        this.setState({text: {...this.state.text, [event.target.id]: {value: event.target.value, id_tag: event.target.id}}})
        if (event.target.id in this.state.text){
            axios.patch(`http://localhost:3001/texts/${this.state.text[event.target.id].id}`, {value: event.target.value, id_tag: event.target.id, sheet: "4"})
    }
        else {
            axios.post("http://localhost:3001/texts", {value: event.target.value, id_tag: event.target.id, sheet: "4" })
        }
    }
    

    getValue = (id) => {
        for (const i in this.state.text){
            if (this.state.text[i].id_tag === id){
                return this.state.text[i].value;
            }
        }
        return ""
    }
    render(){
        return(
            <div>
                <p>
                    Can you find something in your house that you worked really hard to make?<br />
                    If you can’t find something that you made, can you find something that you worked hard to earn or to save up to buy?<br/>
                    I’ll put my timer on for 5 minutes and I’ll call out when our time is up.  If you do need a few more minutes after the timer goes off, just tell me out loud that you’re still looking so I know you need some more time! <br/>
                </p>
                <div id="instruction_box_number_1_page_4" className="custom_svg demo_box container_for_medium_margin">
                    <p className='top_line_in_instruction_box'>
                    Before sharing your idea, wait to see if the mentee is come up with an example on their own. Then share:  Here is what I made.  This is a <textarea onChange={event => this.handleChange(event)} id="text_box_number_1_page_4_sheet_4" defaultValue={this.getValue("text_box_number_1_page_4_sheet_4")} />  
                    and I made it in / for / when <textarea onChange={event => this.handleChange(event)} id="text_box_number_2_page_4_sheet_4" defaultValue={this.getValue("text_box_number_2_page_4_sheet_4")} />. <br/>
                    When mentee shows you their item, validate them. <br/>
                    You can also ask them question to learn more about the item they showed you 
                    </p>
                </div>
                    

                
            </div>
        )
    }
}

class RedXClicked extends Component {
    render(){
        return(
            <div>
                <p>
                    Ok! <br/>
                    Let’s share our thumbs up and thumbs down for the week. Does that sound good? Or, do you have another idea for a short game? <br/>
                </p>
                <div id="instruction_box_number_2_page_4" className="custom_svg demo_box container_for_medium_margin">
                    <p>
                        If they have an idea for another short game, you can do that. <br/>
                        If they do not, explain: <br/>

                        Thumbs up is something that made you happy, or something about the week that you enjoyed/ that felt good.  <br/> 

                        A thumbs down can be any stress or anything that’s upsetting about this week.<br/>

                        Remember to show understanding when your mentee shares.<br/>
                    </p>  
                </div>
                
            </div>
        )
    }
}

export default Page4;
