import React, { Component } from 'react';

import pressrecord from '../../pictures/pressrecord.png'; 
import check from "../../pictures/check.png";
import redX from "../../pictures/redx.png";
import axios from 'axios';
// import ContentEditable from 'react-contenteditable';

class Page2 extends Component {
    state = {
        handleclick: {
        check_words: null,
        x_words: ''
        },
        text: {}
    }
        handleClick = (e, letter) => {
            if (letter === 'c'){
                this.setState({handleclick: {check_words: <Checked />, x_words: ''}})
            }
            else if (letter === 'x'){
                this.setState({handleclick: {check_words : null, x_words: "Go to the next page"}})
            }
        }

        handleScroll=()=>{
            window.scroll({top:0, behavior:'smooth'})
        }
        
        componentDidMount() {
            this.handleScroll()
            axios.get("http://localhost:3001/texts")
            .then(res => {
                const texts = res.data;
                for (const txt of texts){
                    if (txt.sheet === "5"){
                        this.setState({
                            text: {...this.state.text, [txt.id_tag]: txt}
                        })
                    }
                }
                  
        })
    }

        handleChange = (event) => {
            this.setState({text: {...this.state.text, [event.target.id]: {value: event.target.value, id_tag: event.target.id}}})
            if (event.target.id in this.state.text){
                axios.patch(`http://localhost:3001/texts/${this.state.text[event.target.id].id}`, {value: event.target.value, id_tag: event.target.id, sheet: "5"})
        }
            else {
                axios.post("http://localhost:3001/texts", {value: event.target.value, id_tag: event.target.id, sheet: "5" })
            }
        }
        

        getValue = (id) => {
            for (const i in this.state.text){
                if (this.state.text[i].id_tag === id){
                    return this.state.text[i].value;
                }
            }
            return ""
        }

    render() {
        return (
            <div className="sheet">
                <img id="pressrecord"src={pressrecord} alt="Press Record" className="left" />
                <h1 id="p2title" className="bold center">Starting the Session</h1>
                <div id="page2" className="left">
                    
                    <br /><br />
                    <h2 >Hi, it is good to talk with you again!</h2>
                    <h2>As a reminder, I am audio recording this session so I can remember what you say and make sure I am doing a good job.</h2>
                    <br />
                    <h2>I had a good time talking with you last week.</h2>
                    <h2>I enjoyed : </h2>
                    <div id="np_1_2">
                        <textarea onChange={event => this.handleChange(event)} id="nt_1_2" defaultValue={this.getValue("nt_1_2")} />
                    </div>
                    <h2>Was there anything last week you wanted to talk about more?</h2>
                    <br/><br/>
                    <img className="check" src={check} alt="Check" onClick={(event) => this.handleClick(event, 'c')} /><h2 className="menSay">Your mentee says yes</h2>
                        <br/>
                        <h2 className="choicePicked">{this.state.handleclick.check_words}</h2>
                        <br/><br/>
                        <img className="redX" src={redX} alt="Red X" onClick={(event) => this.handleClick(event, 'x')} /><h2 className="menSay">Your mentee says no</h2>
                        <br/>
                        <h2 className="choicePicked">{this.state.handleclick.x_words}</h2>
                    
                </div>
            </div>
        )
    }
    
}

class Checked extends Component{
    render(){
        return(
            <div id="checked">
                <h2>Talk to your mentee about this topic for up to 5 minutes.</h2>
                <br/><br/>
                <h2>Once you and your mentee have talked for about five minutes, you can thank them for sharing.  Politely suggest that it’s time to transition to hearing about the plan for the day.  If the mentee still wants to talk, suggest “pausing” for now and coming back to the conversation at the end of the peer mentoring session. </h2>
            </div>
        )
    }
}
export default Page2;
