import React, { Component } from 'react';

import check from "../../pictures/check.png";
import redX from "../../pictures/redx.png";

import axios from 'axios';


class Page4 extends Component {
    
    state = {
        text: {}
    }

    componentDidMount = () => {
        axios.get("http://localhost:3001/texts")
        .then(res => {
            const texts = res.data;
            for (const txt of texts){
                if (txt.sheet === "4"){
                    this.setState({
                        text: {...this.state.text, [txt.id_tag]: txt}
                    })
                }
            }      
        })
    }

    handleChange = (event) => {
        this.setState({text: {...this.state.text, [event.target.id]: {value: event.target.value, id_tag: event.target.id}}})
        if (event.target.id in this.state.text){
            axios.patch(`http://localhost:3001/texts/${this.state.text[event.target.id].id}`, {value: event.target.value, id_tag: event.target.id, sheet: "4"})
    }
        else {
            axios.post("http://localhost:3001/texts", {value: event.target.value, id_tag: event.target.id, sheet: "4" })
        }
    }
    

    getValue = (id) => {
        for (const i in this.state.text){
            if (this.state.text[i].id_tag === id){
                return this.state.text[i].value;
            }
        }
        return ""
    }
        

    render() {
        return (
            <div className="sheet">
                <h1 id="p4title" className="bold center">Icebreaker game/fun activity</h1>
                <div id="page4" className="left container_for_medium_margin">
                    <h2>Would you like to do another scavenger hunt show and tell? </h2>
                    <div className="container_for_medium_margin">
                        <img className="check" src={check} alt="Check" onClick={(event) => this.handleClick(event, 'c')} /><h2 className="what_does_your_mentor_say">Your mentee says yes</h2>
                        <br/>
                        <h2 className="choicePicked">{this.state.handleclick.words_that_appear_when_you_click_green_check}</h2>
                        <br/><br/>
                        <img className="redX" src={redX} alt="Red X" onClick={(event) => this.handleClick(event, 'x')} /><h2 className="what_does_your_mentor_say">Your mentee says no</h2>
                        <br/>
                        <h2 className="choicePicked">{this.state.handleclick.words_that_appear_when_you_click_red_x}</h2>
                    </div>
                </div>
            </div>
        )
    }
}



export default Page4;
