import React, { Component } from 'react';

import axios from 'axios';


class Page10 extends Component {

    state = {
        text: {}
    }

    handleScroll=()=>{
        window.scroll({top:0,behavior:'smooth'})
    
    }
    componentDidMount() {
        this.handleScroll()
        axios.get("http://localhost:3001/texts")
        .then(res => {
            const texts = res.data;
            for (const txt of texts){
                if (txt.sheet === "3"){
                    this.setState({
                        text: {...this.state.text, [txt.id_tag]: txt}
                    })
                }
            }
              
    })
}

    handleChange = (event) => {
        this.setState({text: {...this.state.text, [event.target.id]: {value: event.target.value, id_tag: event.target.id}}})
        if (event.target.id in this.state.text){
            axios.patch(`http://localhost:3001/texts/${this.state.text[event.target.id].id}`, {value: event.target.value, id_tag: event.target.id, sheet: "3"})
    }
        else {
            axios.post("http://localhost:3001/texts", {value: event.target.value, id_tag: event.target.id, sheet: "3" })
        }
    }

    getValue = (id) => {
        for (const i in this.state.text){
            if (this.state.text[i].id_tag === id){
                return this.state.text[i].value;
            }
        }
        return ""
    }
  
    render() {
        return (
            <div className="sheet">
                <div id="page10" className="left">
                    <h1 id="p1title" className="bold center">Time for questions</h1>
                    <br />
                    <h2>We are almost done for today.</h2>
                    <h2>Do you have any questions or comments about what we talked about? </h2>
                    <br/><br/>
                    <h2>Write their question here: </h2>
                    <div id="np_1_10">
                    <textarea onChange={event => this.handleChange(event)} id="nt_1_10" defaultValue={this.getValue("nt_1_10")} />
                    </div>
                    <br/><br/><br/>
                    <div id="inst_1_10" className="custom_svg demo_box">
                        <h2 className="in_inst"><em>If they ask you a question that you don’t know the answer to, say</em>: I don’t know the answer to that question. I will ask someone and tell you the answer next time we talk. </h2>
                        
                    </div>    
                </div>
            </div>
        )
    }
}
export default Page10;
