import { Component } from "react";
import axios from 'axios';

import './users.css'

class Login extends Component{

    constructor(props) {
        super(props);
        this.state = { 
          username: '',
          password: '',
          errors: ''
         };
      }
    handleChange = (event) => {
        const {name, value} = event.target
        console.log(name);
        console.log(value);
        this.setState({
          [name]: value
        })
      };
      handleSubmit = (event) => {
        event.preventDefault()
        const user = {
          username: this.state.username,
          password: this.state.password
        }
        
    axios.post('http://localhost:3001/login', {user}, {withCredentials: true})
        .then(response => {
          console.log(response)
          if (response.data.logged_in) {
            this.props.handleLogin(response.data)
            this.redirect()
          } else {
            this.setState({
              errors: response.data.errors
            })
          }
        })
        .catch(error => console.log('api errors:', error))
      };
    redirect = () => {
        this.props.history.push('/')
      }
    handleErrors = () => {
        return (
          <div>
            <ul>
            {this.state.errors.map(error => {
            return <li key={error}>{error}</li>
              })}
            </ul>
          </div>
        )
      };

    render(){
        return(
            <div id="userDiv">
                <h1 className="center">Login</h1>
                <div id="formDiv">
                    <form id="loginForm">
                        <h2>Username: <input onChange={event => this.handleChange(event)} type="text" className="text" id="username" name="username"></input></h2>
                        <br/>
                        <h2>Password: <input type="password" className="text" id="password" name="password"></input></h2>
                        <input name="submit" value="Submit" type="submit" id="submit" onClick={event => this.handleSubmit(event)}></input>
                    </form>
                </div>
            </div>
        )
    }
}

export default Login;
