import { Component } from 'react';
import check from "../../pictures/check.png";
import redX from "../../pictures/redx.png";


class Page6 extends Component {
    state = {
        check_words: null,
        x_words: null,
        
    }
    handleClick = (e, letter) => {
        if (letter === 'c'){
                this.setState({check_words: <CheckMark choice={0} />, x_words: ''})
            }
            else if (letter === 'x'){
                this.setState({check_words : '', x_words: <CheckMark choice={1}/>})
            }
       
    }
    handleScroll=()=>{
        window.scroll({top:0,behavior:'smooth'})
    
    }
    componentDidMount() {
        this.handleScroll()
  }

    render() {
        return (
            <div className="sheet">
                <div id="page6">
                    <h1 id="p6title" className="bold center">Self-monitoring</h1>
                    <br /><br />
                    <h2 className="left">Today we’re going to talk more about noticing symptoms. </h2>
                    <h2 className="left">Sometimes it can be hard to know how you are feeling or to notice your feelings. It can take practice.  When you know how you are feeling, you can do something to feel better.</h2>
                    <h2 className="left">Figuring out how you are feeling is called “self-monitoring.”  Have you heard of this before?</h2>
                    <div id="pics">
                        <img className="check" src={check} alt="Check" onClick={(event) => this.handleClick(event, 'c')} /><h2 className="menSay">Your mentee says yes</h2>
                        <br/>
                        <h2 className="choicePicked">{this.state.check_words}</h2>
                        <br/><br/>
                        <img className="redX" src={redX} alt="Red X" onClick={(event) => this.handleClick(event, 'x')} /><h2 className="menSay">Your mentee says no</h2>
                        <br/>
                        <h2 className="choicePicked">{this.state.x_words}</h2>
                        <br/><br/>
                    </div>
                   </div>
            </div>
        )
    }


}

class CheckMark extends Component {

    picker = () => {
        /* Determines what to choose as the shown text based on if you clicked on the check or the x */
        if (this.props.choice === 0){//The check was pressed
            return (
                <div>
                    <h2>That's great! How do you describe self-monitoring?</h2>
                    <div id="inst_1_6" className="custom_svg demo_box">
                        <h2 className="in_inst">Give your mentee positive feedback on their definition. </h2>
                    </div>
                    <h2>My definition of self-monitoring is: paying attention to how you are feeling. This might mean thinking about how your body feels. It might also mean identifying the thoughts you have in your head. You might have happy thoughts, or you might have anxious or sad thoughts. </h2>
                </div> 
            )
        }
        else if (this.props.choice === 1){//The x was pressed
            return (
                <div>
                    <h2>That’s ok! I’ll tell you about it!</h2>
                    <h2>Self-monitoring means paying attention to how you are feeling. This might mean thinking about how your body feels. It might also mean identifying the thoughts you have in your head. You might have happy thoughts, or you might have anxious or sad thoughts. </h2>
                </div> 
            )
        }
    }

    render () {
        return (
            <div className="left">
                {this.picker()}
                <br/>
                <h2>When you self-monitor, you can learn more about how you are feeling. This can help you change your thoughts and feelings to feel better.</h2>
                <br/>
                <h2>You can get really good at self-monitoring if you practice.</h2>
            </div>
        )
    }
}


export default Page6;
