import { Component } from 'react';

import activities from '../../pictures/activities.png';

import axios from 'axios';

import '../use_external_css.scss';

class Page6 extends Component {
    state = {
        
        text: {}
        }
    
        handleScroll=()=>{
            window.scroll({top:0,behavior:'smooth'})
        
        }
        componentDidMount = () => {
            axios.get("http://localhost:3001/texts")
            .then(res => {
                const texts = res.data;
                for (const txt of texts){
                    if (txt.sheet === "4"){
                        this.setState({
                            text: {...this.state.text, [txt.id_tag]: txt}
                        })
                    }
                }      
            })
        }
    
        handleChange = (event) => {
            this.setState({text: {...this.state.text, [event.target.id]: {value: event.target.value, id_tag: event.target.id}}})
            if (event.target.id in this.state.text){
                axios.patch(`http://localhost:3001/texts/${this.state.text[event.target.id].id}`, {value: event.target.value, id_tag: event.target.id, sheet: "4"})
        }
            else {
                axios.post("http://localhost:3001/texts", {value: event.target.value, id_tag: event.target.id, sheet: "4" })
            }
        }
        
    
        getValue = (id) => {
            for (const i in this.state.text){
                if (this.state.text[i].id_tag === id){
                    return this.state.text[i].value;
                }
            }
            return ""
        }
            
    
        render() {
            return (
                <div className="sheet">                    
                    <h1 id="p6title" className="bold center">Doing the card sort activity</h1>
                    <div id="page6" className="left container_for_medium_margin">
                        <div id="instructions_and_picture_pairing">
                            <div className='custom_svg demo_box' id='instruction_box_number_1_page_6'>
                                    <p className='top_line_in_instruction_box'>
                                        Do the card sort activity with your mentee.<br/><br/>
                                        Send your mentee the card sort link: <br/>
                                        Link: <br/><br/>
                                        Ask them to share their screen.
                                    </p>
                            </div>
                            <img src={activities} alt="Activities to do" id="activities_page_6" />
                        </div>

                        <div className='container_for_large_margin'>
                            <p>
                                <span className='underline_text'>Card sort directions</span><br/>
                                First, we will think about activities you do and do not like.<br/> 
                                If you like the type of activity, you will put the card in the “like” pile. <br/>
                                If you do not like the activity, you will put the card in the “do not like” pile.
                            </p>
                        </div>

                        <div className='custom_svg demo_box container_for_small_margin' id='instruction_box_number_2_page_6'>
                                    <p className='top_line_in_instruction_box'>
                                        <span className='underline_text'>Prompts-things you can say if your mentee needs help</span> <br/>
                                        <ul>
                                            <li className='li'>How does the activity make you feel?</li>
                                            <li className='li'>Is the activity fun?</li>
                                            <li className='li'>Is the activity interesting or boring?</li>
                                        </ul><br/>
                                        <em>When your mentee is finished, say</em>: Good job! Now we are ready for the next part of the activity!
                                    </p>
                            </div>

                        <div className='container_for_large_margin'>
                            <p>
                                Now that we are done thinking about if you like the activities, we will think about how they make you feel.
                            </p>
                        </div>
                        <div className='container_for_medium_margin'>
                            <form>
                                <div className="use-external-css" id="form_fields_div">
                                    For example, sometimes I do feel<br/><br/>
                                    <span className="inline-flex flex-col">
                                        <input
                                            onChange={event => this.handleChange(event)}
                                            defaultValue={this.getValue("text_box_number_1_page_6")}
                                            className="border-b border-black"
                                            type="text"
                                            name="text_box_number_1_page_6"
                                            id="text_box_number_1_page_6"
                                        />
                                        <label for="text_box_number_1_page_6">(a feeling you don’t like)</label>
                                    </span>
                                    <span>. I do</span>
                                    <span className="inline-flex flex-col">
                                        <input
                                            onChange={event => this.handleChange(event)}
                                            defaultValue={this.getValue("text_box_number_2_page_6")}
                                            className="border-b border-black"
                                            type="text"
                                            name="text_box_number_2_page_6"
                                            id="text_box_number_2_page_6"
                                        />
                                        <label for="text_box_number_2_page_6">(an activity that makes you feel better)</label>
                                    </span>

                                    <span>to make me feel less</span>

                                    <span className="inline-flex flex-col">
                                    <input
                                        onChange={event => this.handleChange(event)}
                                        defaultValue={this.getValue("text_box_number_3_page_6")}
                                        className="border-b border-black"
                                        type="text"
                                        name="text_box_number_3_page_6"
                                        id="text_box_number_3_page_6"
                                    />
                                    <label for="text_box_number_3_page_6"
                                        >(the feeling you don’t like)</label
                                    >
                                    </span>
                                    <span>That means that</span>

                                    <span className="inline-flex flex-col">
                                    <input
                                        className="border-b border-black"
                                        type="text"
                                        name="text_box_number_3_page_6"
                                        id="text_box_number_3_page_6"
                                    />
                                    <label for="text_box_number_3_page_6"
                                        >(the activity that makes you feel better)</label
                                    >
                                    </span>
                                    <span>is a coping strategy.</span>
                                </div>
                            </form>  
                        </div>
                    </div>
                </div>
            )
        }
}   


export default Page6;
