import React, { Component } from 'react';

import body from '../../pictures/body.png'
import axios from 'axios';


class Page7 extends Component {

    state = {
        text: {}
    }
    
    handleScroll=()=>{
        window.scroll({top:0,behavior:'smooth'})
    }
    
    componentDidMount() {
        this.handleScroll()
        axios.get("http://localhost:3001/texts")
        .then(res => {
            const texts = res.data;
            for (const txt of texts){
                if (txt.sheet === "5"){
                    this.setState({
                        text: {...this.state.text, [txt.id_tag]: txt}
                    })
                }
            }
              
    })
}

    handleChange = (event) => {
        this.setState({text: {...this.state.text, [event.target.id]: {value: event.target.value, id_tag: event.target.id}}})
        if (event.target.id in this.state.text){
            axios.patch(`http://localhost:3001/texts/${this.state.text[event.target.id].id}`, {value: event.target.value, id_tag: event.target.id, sheet: "5"})
    }
        else {
            axios.post("http://localhost:3001/texts", {value: event.target.value, id_tag: event.target.id, sheet: "5" })
        }
    }

    getValue = (id) => {
        for (const i in this.state.text){
            if (this.state.text[i].id_tag === id){
                return this.state.text[i].value;
            }
        }
        return ""
    }
  
    render() {
        return (
            <div className="sheet">
                <div id="page7" className="left">
                    <h1 id="p1title" className="bold center container_for_medium_margin">Review</h1>
                    <div id="bodyScanImageDiv">
                        <p>
                            Last week, we worked on the body scan worksheet. 
                            <br/><br/><br/>
                            Did you try using the body scan worksheet on your own?
                            <br/><br/><br/>
                            What do you think about it?
                            <br/>
                            There are no right or wrong answers.
                        </p>
                        <img src={body} alt="Body" id='body7' />
                    </div>
                    
                    <div className='container_for_medium_margin'>
                        <p>Is any part of it hard?</p>
                            <textarea onChange={event => this.handleChange(event)} id="text_box_number_1_page_7" defaultValue={this.getValue("text_box_number_1_page_7")} className='textarea' /><br/>
                            <p> Is there anything you don't like about it?<br/><br/></p>
                            <textarea onChange={event => this.handleChange(event)} id="text_box_number_2_page_7" defaultValue={this.getValue("text_box_number_2_page_7")} className='textarea' /><br/>
                            Is there any way I can help make it easier for you?<br/><br/>
                            <textarea onChange={event => this.handleChange(event)} id="text_box_number_3_page_7" defaultValue={this.getValue("text_box_number_3_page_7")} className='textarea' /><br/>
                            Let's practice using it again together. 
                            <br/><br/>
                            How are you feeling right now?
                    </div>
                
                </div>
            </div>
        )
    }
}
export default Page7;
